ionKeyViewmodel.dart';import 'package:sco_v1/viewModel/apply_scholarship/find_draft_by_draft_id_viewmodel.dart';import 'package:sco_v1/viewModel/apply_scholarship/saveAsDraftViewmodel.dart';import 'package:sco_v1/viewModel/apply_scholarship/submitApplicationViewmodel.dart';import 'package:sco_v1/viewModel/authentication/get_roles_viewModel.dart';import 'package:sco_v1/viewModel/language_change_ViewModel.dart';import 'package:sco_v1/viewModel/services/auth_services.dart';import 'package:sco_v1/viewModel/services/media_services.dart';import 'package:sco_v1/viewModel/services/permission_checker_service.dart';import '../../data/response/status.dart';import '../../models/apply_scholarship/FillScholarshipFormModels.dart';import '../../resources/app_colors.dart';import '../../resources/app_text_styles.dart';import '../../resources/components/account/Custom_inforamtion_container.dart';import '../../utils/constants.dart';import '../../viewModel/account/get_list_application_status_viewmodel.dart';import '../../viewModel/apply_scholarship/attach_file_viewmodel.dart';import '../../viewModel/apply_scholarship/deleteDraftViewmodel.dart';import '../../viewModel/apply_scholarship/getAllActiveScholarshipsViewModel.dart';import '../../viewModel/services/alert_services.dart';import '../../viewModel/services/navigation_services.dart';import 'dart:convert';import 'form_view_Utils.dart';class FillScholarshipFormView extends StatefulWidget {   String? selectedScholarshipConfigurationKey;  String? draftId;   List<GetAllActiveScholarshipsModel?>? getAllActiveScholarships;  FillScholarshipFormView({super.key, this.selectedScholarshipConfigurationKey, this.draftId, this.getAllActiveScholarships});  @override  _FillScholarshipFormViewState createState() =>      _FillScholarshipFormViewState();}class _FillScholarshipFormViewState extends State<FillScholarshipFormView>    with MediaQueryMixin {  /// my required services  late NavigationServices _navigationService;  late MediaServices _mediaServices;  late AlertServices _alertService;  // late PermissionServices _permissionServices;  // late AuthService _authServices;  /// initialize the services  void _initializeServices() {    final GetIt getIt = GetIt.instance;    _navigationService = getIt.get<NavigationServices>();    _alertService = getIt.get<AlertServices>();    _mediaServices = getIt.get<MediaServices>();    // _permissionServices = getIt.get<PermissionServices>();    // _authServices = getIt.get<AuthService>();  }  /// Controller for managing the pages  final PageController _pageController = PageController();  int _currentSectionIndex = 0; /// Track current section index  int totalSections = 1; /// Assume dynamic number of sections  /// Function to navigate to the next section  void nextSection() {    if (_currentSectionIndex < totalSections - 1) {      setState(() {        _currentSectionIndex++;        _pageController.nextPage(          duration: const Duration(milliseconds: 300),          curve: Curves.easeInOut,        );      });    }  }  /// Function to navigate to the previous section  void previousSection() {    if (_currentSectionIndex > 0) {      setState(() {        _currentSectionIndex--;        _pageController.previousPage(          duration: const Duration(milliseconds: 300),          curve: Curves.easeInOut,        );      });    }  }  /// Track filled sections  List<bool> filledSections = [];  /// update sections count method  void updateSections(int sections) {    setState(() {      filledSections = List.filled(sections, false);    });  }  dynamic draftId;  /// Function to save draft  Future<void> saveDraft() async {    /// using modelProgressHud    setProcessingMode(status: true);    _finalForm();    final saveDraftProvider = Provider.of<SaveAsDraftViewmodel>(context, listen: false);    await saveDraftProvider.saveAsDraft(form: form, applicationNumber: draftId?.toString() ?? '0');    if(saveDraftProvider.apiResponse.status == Status.COMPLETED){      draftId = saveDraftProvider.apiResponse.data?.data?.applicationNumber ?? '0';    }    setProcessingMode(status: false);  }  /// scholarship title  String _scholarshipTitle = '';  /// selected scholarship:  GetAllActiveScholarshipsModel? _selectedScholarship;  /// function to set selected scholarship Future<bool>  _getSelectedScholarship(langProvider) async{    try {      final allScholarships =  Provider.of<GetAllActiveScholarshipsViewModel>(context,listen:false);      await allScholarships.getAllActiveScholarships(context: context, langProvider: langProvider);      if(allScholarships.apiResponse.status == Status.COMPLETED && widget.selectedScholarshipConfigurationKey != null){        /// Get selected scholarship from the provided key        _selectedScholarship =  allScholarships.apiResponse.data?.firstWhere((scholarship) => scholarship.configurationKey == widget.selectedScholarshipConfigurationKey);        /// refreshing the selected scholarship        setState(() {        });        return true;      }      // Handling the form when opening from application statuses      if(widget.draftId != null){        /// Fetch draft by draft id        final draftProvider = Provider.of<FindDraftByDraftIdViewmodel>(context, listen: false);        await draftProvider.findDraftByDraftId(draftId: widget.draftId.toString());        if(draftProvider.apiResponse.status == Status.COMPLETED){          _selectedScholarship = allScholarships.apiResponse.data?.firstWhere((scholarship) => scholarship.configurationKey == draftProvider.apiResponse.data?.scholarshipConfId);          draftId = draftProvider.apiResponse.data?.applicationId;          setState(() {          });          return true;        }      }        return false;     } catch (e) {      _alertService.toastMessage("An error occurred while trying to fetch the selected scholarship. Please try again.");      return false;    }  }  /// creating the title for scholarship(Name of the scholarship visible to the user) and number of tabs using multiple conditions  void _setTitleAndTotalSections({required BuildContext context}) {   final localization = AppLocalizations.of(context)!;    try {      if (_selectedScholarship != null) {        /// Internal Bachelor        if (_selectedScholarship!.configurationKey == 'SCOUGRDINT') {          setState(() {            _scholarshipTitle = localization.internalBachelor;            totalSections = 7; /// Example number of sections          });        }        /// Internal Postgraduate        else if (_selectedScholarship!.configurationKey == 'SCOPGRDINT') {          setState(() {            _scholarshipTitle = localization.internalPostgraduate;            totalSections = 8; /// Example number of sections          });        }        /// Meterological scholarship in UAE        else if (_selectedScholarship!.configurationKey == 'SCOMETLOGINT') {          setState(() {            _scholarshipTitle = localization.internalMeterological;            totalSections = 7; /// Example number of sections          });        }        /// Bachelor Graduation scholarship outside UAE        else if (_selectedScholarship!.configurationKey == 'SCOUGRDEXT') {          setState(() {            _scholarshipTitle = localization.externalBachelor;            totalSections = 7; /// Example number of sections          });        }        /// Bachelor Graduation scholarship outside UAE        else if (_selectedScholarship!.configurationKey == 'SCONLUEXT') {          setState(() {            _scholarshipTitle = "Under Graduate External NLU";            totalSections = 7; /// Example number of sections          });        }        /// Bachelor Post Graduation scholarship outside UAE        else if (_selectedScholarship!.configurationKey == 'SCOPGRDEXT') {          setState(() {            _scholarshipTitle = localization.externalPostgraduate;            totalSections = 8; /// Example number of sections          });        }        /// External Doctors        else if (_selectedScholarship!.configurationKey == 'SCODDSEXT') {          setState(() {            _scholarshipTitle = localization.externalDoctorsOutsideUae;            totalSections = 8; /// Example number of sections          });        }        /// University Preparation Scholarship        else if (_selectedScholarship!.configurationKey == 'SCOUPPEXT') {          setState(() {            _scholarshipTitle = localization.universityPreparationProgram;            totalSections = 6; /// Example number of sections          });        }        /// Actuarial Science Mission - Bachelor's Degree        else if (_selectedScholarship!.configurationKey == 'SCOACTUGRD') {          setState(() {            _scholarshipTitle = localization.actuarialScience;            totalSections = 7; /// Example number of sections          });        }        /// Medical Profession Program        else if (_selectedScholarship!.configurationKey == 'SCOAHCPEXT') {          setState(() {            _scholarshipTitle = "Medical Profession Program"; /// TODO: fix name for localization            totalSections = 8; /// Example number of sections          });        }        /// Post graduation external medicine        // else if (_selectedScholarship!.configurationKey == 'SCOPGRDMDEXT') {        //   setState(() {        //     _scholarshipTitle = "Post Graduation External medicin"; /// TODO: fix name for localization        //     totalSections = 8; /// Example number of sections        //   });        // }      } else {        throw "_selectedScholarship is found null";      }    } catch (e) {      // _alertService.toastMessage("An error occurred while trying to set the title & number of tabs of the selected scholarship. Please try again.");      // debugPrint("Something went wrong: $e");    }  }  final _notifier = ValueNotifier<AsyncSnapshot<void>>(const AsyncSnapshot.waiting());  // Selected Checklist code for attachments  dynamic _selectedCheckListCode;  ///  Initializer to process initial data  void _initializer() {    WidgetsBinding.instance.addPostFrameCallback((callback) async {      _notifier.value = const AsyncSnapshot.withData(ConnectionState.waiting, null);      final langProvider = Provider.of<LanguageChangeViewModel>(context, listen: false);      /// get selected scholarship      await  _getSelectedScholarship(langProvider);      /// set title and total sections      _setTitleAndTotalSections(context: context);      /// Initialize filledSections based on the current totalSections      updateSections(totalSections);      /// *------------------------------------------ Initialize dropdowns start ------------------------------------------------------------------*      /// Check and populate dropdowns only if the values exist      if (Constants.lovCodeMap['COUNTRY']?.values != null) {        _nationalityMenuItemsList = populateCommonDataDropdown(            menuItemsList: Constants.lovCodeMap['COUNTRY']!.values!,            provider: langProvider,            textColor: AppColors.scoButtonColor);      }      if (Constants.lovCodeMap['GENDER']?.values != null) {        _genderMenuItemsList = populateCommonDataDropdown(            menuItemsList: Constants.lovCodeMap['GENDER']!.values!,            provider: langProvider,            textColor: AppColors.scoButtonColor);      }      if (Constants.lovCodeMap['MARITAL_STATUS']?.values != null) {        _maritalStatusMenuItemsList = populateCommonDataDropdown(            menuItemsList: Constants.lovCodeMap['MARITAL_STATUS']!.values!,            provider: langProvider,            textColor: AppColors.scoButtonColor);      }      if (Constants.lovCodeMap['RELATIONSHIP_TYPE']?.values != null) {        _relationshipTypeMenuItemsList = populateCommonDataDropdown(            menuItemsList: Constants.lovCodeMap['RELATIONSHIP_TYPE']!.values!,            provider: langProvider,            textColor: AppColors.scoButtonColor);      }      if (Constants.lovCodeMap['PHONE_TYPE']?.values != null) {        _phoneNumberTypeMenuItemsList = populateCommonDataDropdown(            menuItemsList: Constants.lovCodeMap['PHONE_TYPE']!.values!,            provider: langProvider,            textColor: AppColors.scoButtonColor);      }      if (Constants.lovCodeMap['EMIRATES_ID']?.values != null) {        _familyInformationEmiratesMenuItemsList = populateCommonDataDropdown(            menuItemsList: Constants.lovCodeMap['EMIRATES_ID']!.values!,            provider: langProvider,            textColor: AppColors.scoButtonColor);      }      if (Constants.lovCodeMap['ADDRESS_TYPE']?.values != null) {        _addressTypeMenuItemsList = populateCommonDataDropdown(            menuItemsList: Constants.lovCodeMap['ADDRESS_TYPE']!.values!,            provider: langProvider,            textColor: AppColors.scoButtonColor);      }      if (Constants.lovCodeMap['HIGH_SCHOOL_LEVEL']?.values != null) {        _highSchoolLevelMenuItemsList = populateCommonDataDropdown(            menuItemsList: Constants.lovCodeMap['HIGH_SCHOOL_LEVEL']!.values!,            provider: langProvider,            textColor: AppColors.scoButtonColor);      }      if (Constants.lovCodeMap['HIGH_SCHOOL_TYPE']?.values != null) {        _highSchoolTypeMenuItemsList = populateCommonDataDropdown(            menuItemsList: Constants.lovCodeMap['HIGH_SCHOOL_TYPE']!.values!,            provider: langProvider,            textColor: AppColors.scoButtonColor);      }      if (Constants.lovCodeMap['SUBJECT']?.values != null) {        _highSchoolSubjectsItemsList = populateUniqueSimpleValuesFromLOV(            menuItemsList: Constants.lovCodeMap['SUBJECT']!.values!,            provider: langProvider,            textColor: AppColors.scoButtonColor);      }      if (Constants.lovCodeMap['DDS_GRAD_LEVEL#SIS_GRAD_LEVEL']?.values !=          null) {        _graduationLevelDDSMenuItems = populateCommonDataDropdown(            menuItemsList:                Constants.lovCodeMap['DDS_GRAD_LEVEL#SIS_GRAD_LEVEL']!.values!,            provider: langProvider,            textColor: AppColors.scoButtonColor);      }      if (Constants.lovCodeMap['GRADUATION_LEVEL']?.values != null) {        _graduationLevelMenuItems = populateCommonDataDropdown(            menuItemsList: Constants.lovCodeMap['GRADUATION_LEVEL']!.values!,            provider: langProvider,            textColor: AppColors.scoButtonColor);      }      if (Constants.lovCodeMap['BATCH#${_selectedScholarship?.acadmicCareer}']              ?.values !=          null) {        _caseStudyYearDropdownMenuItems = populateCommonDataDropdown(menuItemsList: Constants.lovCodeMap['BATCH#${_selectedScholarship?.acadmicCareer}']!.values!,            provider: langProvider,            textColor: AppColors.scoButtonColor);      }      if (Constants.lovCodeMap['EXAMINATION#${_selectedScholarship?.acadmicCareer}']?.values != null) {        _requiredExaminationDropdownMenuItems = populateCommonDataDropdown(menuItemsList: Constants.lovCodeMap['EXAMINATION#${_selectedScholarship?.acadmicCareer}']!.values!,            provider: langProvider,            textColor: AppColors.scoButtonColor);      }      if (Constants.lovCodeMap['ACAD_PROG_PGRD']?.values != null) {        _acadProgramPgrdMenuItemsList = populateCommonDataDropdown(            menuItemsList: Constants.lovCodeMap['ACAD_PROG_PGRD']!.values!,            provider: langProvider,            textColor: AppColors.scoButtonColor);      }      if (Constants.lovCodeMap['ACAD_PROG_DDS']?.values != null) {        _acadProgramDdsMenuItemsList = populateCommonDataDropdown(            menuItemsList: Constants.lovCodeMap['ACAD_PROG_DDS']!.values!,            provider: langProvider,      