r(),              supportedFileTypeController: TextEditingController(),              maxFileSizeController: TextEditingController(),              applicationDetailIdController: TextEditingController(),              emiratesIdController: TextEditingController(),              isApprovedController: TextEditingController(),              fileIdController: TextEditingController(),              fileTypeController: TextEditingController(),              newFileController: TextEditingController(),              // Focus Nodes              processCdFocusNode: FocusNode(),              documentCdFocusNode: FocusNode(),              userFileNameFocusNode: FocusNode(),              commentFocusNode: FocusNode(),              requiredFocusNode: FocusNode(),              fileUploadedFocusNode: FocusNode(),              heightFocusNode: FocusNode(),              widthFocusNode: FocusNode(),              errorMessageFocusNode: FocusNode(),              supportedFileTypeFocusNode: FocusNode(),              maxFileSizeFocusNode: FocusNode(),              attachmentNameFocusNode: FocusNode(),              applicationDetailIdFocusNode: FocusNode(),              emiratesIdFocusNode: FocusNode(),              isApprovedFocusNode: FocusNode(),              fileIdFocusNode: FocusNode(),              fileTypeFocusNode: FocusNode(),              newFileFocusNode: FocusNode(),              applictantIdFocusNode: FocusNode(),              descriptionFocusNode: FocusNode(),              base64StringFocusNode: FocusNode(),              // Error Text Variables              processCdError: null,              documentCdError: null,              userFileNameError: null,              commentError: null,              requiredError: null,              fileUploadedError: null,              heightError: null,              widthError: null,              errorMessageError: null,              supportedFileTypeError: null,              maxFileSizeError: null,        