            final documentCD = element.code.toString().split(':').last.toString();            addToMyAttachment(element: element, documentCD: documentCD,processCD: processCD);          }        }      _initializeStudentDetailsModels();      /// setting studyCountry Variable      /// isStudyCountry for majors selection      isStudyCountry = _selectedScholarship?.scholarshipType == 'INT' ? true : false;      _majorsMenuItemsList = getMajors(); /// calling the getMajors method to populate the majors function      /// initializing contact information manually because Phone types are constants for first two index elements      _phoneNumberList.add(PhoneNumber(        phoneTypeController: TextEditingController(text: 'CELL'),        countryCodeController: TextEditingController(),        phoneNumberController: TextEditingController(),        isExisting: false,        preferred: true,        countryCodeFocusNode: FocusNode(),        phoneNumberFocusNode: FocusNode(),        phoneTypeFocusNode: FocusNode(),      ));      _phoneNumberList.add(PhoneNumber(        phoneTypeController: TextEditingController(text: 'GRD'),        countryCodeController: TextEditingController(),        phoneNumberController: TextEditingController(),        preferred: false,        isExisting: false,        countryCodeFocusNode: FocusNode(),        phoneNumberFocusNode: FocusNode(),        phoneTypeFocusNode: FocusNode(),      ));      /// adding at-least one address      _addAddress();      /// add high school :      if(displayHighSchool()) {        _highSchoolList.add(HighSchool(            hsLevelController: TextEditingController(text: '1'),            hsNameController: TextEditingController(),            hsCountryController: TextEditingController(),            hsStateController: TextEditingController(),            yearOfPassingController: TextEditingController(),            hsTypeController: TextEditingController(),            curriculumTypeController: TextEditingController(),            curriculumAverageController: TextEditingController(),            otherHsNameController: TextEditingController(),            passingYearController: TextEditingController(),            maxDateController: TextEditingController(),            disableStateController: TextEditingController(),            isNewController: TextEditingController(text: "true"),            highestQualificationController: TextEditingController(text: 'false'),            hsLevelFocusNode: FocusNode(),            hsNameFocusNode: FocusNode(),            hsCountryFocusNode: FocusNode(),            hsStateFocusNode: FocusNode(),            yearOfPassingFocusNode: FocusNode(),            hsTypeFocusNode: FocusNode(),            curriculumTypeFocusNode: FocusNode(),            curriculumAverageFocusNode: FocusNode(),            otherHsNameFocusNode: FocusNode(),            passingYearFocusNode: FocusNode(),            maxDateFocusNode: FocusNode(),            hsDetails: _highSchoolSubjectsItemsList                .where((element) => !element.code                    .startsWith('OTH')) /// Filter for regular subjects                .map((element) => HSDetails(                      subjectTypeController: TextEditingController(text: element.code.toString()),                      otherSubjectNameController: TextEditingController(),                      gradeController: TextEditingController(),                      subjectTypeFocusNode: FocusNode(),                      otherSubjectNameFocusNode: FocusNode(),                      gradeFocusNode: FocusNode(),              required:  element.required == true || element.required == 'true',            ))                .toList(),            otherHSDetails: _highSchoolSubjectsItemsList                .where((element) => element.code                    .startsWith('OTH')) /// Filter for regular subjects                .map(                  (element) => HSDetails(                    subjectTypeController:                        TextEditingController(text: element.code.toString()),                    gradeController: TextEditingController(),                    otherSubjectNameController: TextEditingController(),                    subjectTypeFocusNode: FocusNode(),                    gradeFocusNode: FocusNode(),                    otherSubjectNameFocusNode: FocusNode(),                    required:  element.required == true || element.required == 'true',                  ),                )                .toList(),            schoolStateDropdownMenuItems: [],            schoolNameDropdownMenuItems: [],            schoolTypeDropdownMenuItems: [],            schoolCurriculumTypeDropdownMenuItems: []));        _highSchoolList.add(HighSchool(            hsLevelController: TextEditingController(text: '3'),            hsNameController: TextEditingController(),            hsCountryController: TextEditingController(),            hsStateController: TextEditingController(),            yearOfPassingController: TextEditingController(),            hsTypeController: TextEditingController(),            curriculumTypeController: TextEditingController(),            curriculumAverageController: TextEditingController(),            otherHsNameController: TextEditingController(),            passingYearController: TextEditingController(),            maxDateController: TextEditingController(),            disableStateController: TextEditingController(),            isNewController: TextEditingController(text: "true"),            highestQualificationController: TextEditingController(text: 'false'),            hsLevelFocusNode: FocusNode(),            hsNameFocusNode: FocusNode(),            hsCountryFocusNode: FocusNode(),            hsStateFocusNode: FocusNode(),            yearOfPassingFocusNode: FocusNode(),            hsTypeFocusNode: FocusNode(),            curriculumTypeFocusNode: FocusNode(),            curriculumAverageFocusNode: FocusNode(),            otherHsNameFocusNode: FocusNode(),            passingYearFocusNode: FocusNode(),            maxDateFocusNode: FocusNode(),            hsDetails: _highSchoolSubjectsItemsList                .where((element) => !element.code                    .startsWith('OTH')) /// Filter for regular subjects                .map((element) => HSDetails(                      subjectTypeController: TextEditingController(text: element.code.toString()),                      otherSubjectNameController: TextEditingController(),                      gradeController: TextEditingController(),                      subjectTypeFocusNode: FocusNode(),                      otherSubjectNameFocusNode: FocusNode(),                      gradeFocusNode: FocusNode(),              required:  element.required == true || element.required == 'true',            ))                .toList(),            otherHSDetails: _highSchoolSubjectsItemsList                .where((element) => element.code                    .startsWith('OTH')) /// Filter for regular subjects                .map(                  (element) => HSDetails(                    subjectTypeController:                        TextEditingController(text: element.code.toString()),                    gradeController: TextEditingController(),                    otherSubjectNameController: TextEditingController(),                    subjectTypeFocusNode: FocusNode(),                    gradeFocusNode: FocusNode(),                    otherSubjectNameFocusNode: FocusNode(),                    required:  element.required == true || element.required == 'true',                  ),                )                .toList(),            schoolStateDropdownMenuItems: [],            schoolNameDropdownMenuItems: [],            schoolTypeDropdownMenuItems: [],            schoolCurriculumTypeDropdownMenuItems: []));      }      /// add graduation detail      _addGraduationDetail();      /// add majors      for (var i = 0; i < 3; i++) {        addMajorWishList();      }      /// add university priority      if (_selectedScholarship?.acadmicCareer != 'SCHL') {        addUniversityPriority();      }      if (!(_selectedScholarship?.acadmicCareer == 'SCHL' ||          _selectedScholarship?.acadmicCareer == 'HCHL')) {        /// add Required Examination        _addRequiredExamination();      }      /// add employment history      if (displayEmploymentHistory()) {        _addEmploymentHistory();      }      /// fetch student profile Information t prefill the user information      /// student profile provider for fetching pre-filling student details and verify that emirate id and email matches the profile      final studentProfileProvider = Provider.of<GetPersonalDetailsViewModel>(context, listen: false);      await studentProfileProvider.getPersonalDetails();      if (studentProfileProvider.apiResponse.status == Status.COMPLETED) {        var data = studentProfileProvider.apiResponse.data?.data?.user;        _arabicName.studentNameController.text = data?.firstName ?? '';        _arabicName.fatherNameController.text = data?.middleName ?? '';        _arabicName.grandFatherNameController.text = data?.middleName2 ?? '';        _arabicName.familyNameController.text = data?.lastName ?? '';        _englishName.studentNameController.text = data?.firstName ?? '';        _englishName.fatherNameController.text = data?.middleName ?? '';        _englishName.grandFatherNameController.text = data?.middleName2 ?? '';        _englishName.familyNameController.text = data?.lastName ?? '';        /// prefill email        _studentEmailController.text = data?.emailAddress ?? '';        /// setting emirates id        _emiratesIdController.text = data?.emirateId?.replaceAll('-', '').trim() ?? '';        /// set date of birth        _dateOfBirthController.text = data?.birthDate ?? '';        /// set Gender        _genderController.text = data?.gender ?? '';        /// set Nationality        _passportNationalityController.text = data?.nationality ?? '';        /// set phone Number        if (_phoneNumberList.isNotEmpty) {          _phoneNumberList[0].phoneNumberController.text = data?.phoneNumber ?? '';        }      }      /// fetch Draft for current Application with Configuration key     if(widget.selectedScholarshipConfigurationKey != null) { await _fetchDraft();}     /// initialize the data by fetching by draft_id     if(widget.draftId != null){       final findDraftByIdProvider = Provider.of<FindDraftByDraftIdViewmodel>(context, listen: false);       if(findDraftByIdProvider.apiResponse.status == Status.COMPLETED){         var draftData = findDraftByIdProvider.apiResponse.data;         final draft = draftData?.status; /// to ensure that draft version is available or not         if (draft != null) {           /// setting draft id ( it is used to store save draft with the same id if it is available before)           draftId = draftData?.applicationId;           _moveWithDraft(draftData);           // _initializeTheDraftFields(draftData);           /// prefill the form with the draft           /// _prefillFormWithDraft(draft);         }       }     }      _notifier.value = const AsyncSnapshot.withData(ConnectionState.done, null);    });  }  /// Function to check if the current date is within the submission period defined by `_selectedScholarship`.  /// Returns `true` if the current date falls within the range, otherwise `false`.  bool isWithinSubmissionPeriod() {    final startTimestamp = _selectedScholarship?.startDate;    final endTimestamp = _selectedScholarship?.endDate;    if (startTimestamp == null || endTimestamp == null) {      return false;    }    final currentTimestamp = DateTime.now().millisecondsSinceEpoch;    return currentTimestamp >= startTimestamp && currentTimestamp <= endTimestamp;  }  /// Check for the draft submission period.  /// If either the start date or draft end date is unavailable, the user is moved back.  /// Otherwise, check if the current date falls within the draft submission period.  bool isWithinDraftSubmissionPeriod() {    // Get the start and draft end timestamps from the selected scholarship    final startTimestamp = _selectedScholarship?.startDate;    final draftEndTimestamp = _selectedScholarship?.draftEndDate;    // If either start date or draft end date is not available, return false    if (startTimestamp == null || draftEndTimestamp == null) {      return false; // Cannot proceed if these values are missing    }    // Get the current timestamp in milliseconds    final currentTimestamp = DateTime.now().millisecondsSinceEpoch;    // Check if the current timestamp is within the start and draft end date range    return currentTimestamp >= startTimestamp && currentTimestamp <= draftEndTimestamp;  }  /// fetch draft and check if available then set draft id and if the user want to move with draft then prefill form with draft and if not then delete draft and set userId null.  Future _fetchDraft() async {    if(widget.selectedScholarshipConfigurationKey != null){  final localization = AppLocalizations.of(context)!;  final langProvider = Provider.of<LanguageChangeViewModel>(context,listen: false);  final textDirection = getTextDirection(langProvider);  /// TO FETCH THE DRAFT BY CONFIGURATION KEY  final findDraftByConfigurationKeyProvider = Provider.of<FindDraftByConfigurationKeyViewmodel>(context, listen: false);  await findDraftByConfigurationKeyProvider.findDraftByConfigurationKey(configurationKey: _selectedScholarship?.configurationKey);  if (findDraftByConfigurationKeyProvider.apiResponse.status == Status.COMPLETED) {    var draftData = findDraftByConfigurationKeyProvider.apiResponse.data;    final draft = draftData?.status; /// to ensure that draft version is available or not    if (draft != null) {      /// setting draft id ( it is used to store save draft with the same id if it is available before)      draftId = draftData?.applicationId;      return showDialog(context: context,          barrierDismissible: false,          builder: (context){        return PopScope(          canPop:false,          child:  Directionality(textDirection: getTextDirection(langProvider), child:  AlertDialog(              backgroundColor: Colors.white,            title: Text(localization.draftApplicationTitle),            content:Text(localization.draftApplicationFound),              actionsAlignment: MainAxisAlignment.end,              actions: [                /// delete draft                Consumer<DeleteDraftViewmodel>(                  builder: (context, provider, _) {                    return provider.apiResponse.status == Status.LOADING                        ? Utils.cupertinoLoadingIndicator()                        :                    CustomButton(buttonName: localization.no,buttonColor: Colors.white,borderColor: Colors.grey,textColor: Colors.grey, isLoading: false, textDirection: textDirection, onTap:                        () async {                      // Step 3: Check if the current date falls within the submission period (start and end date)                      if (isWithinSubmissionPeriod()) {                        // Case: Current date is within the allowed submission period                        // Proceed to delete draft permanently if user chooses to move with fresh submission                        await provider.deleteDraft(draftId: draftId ?? '');                        // Step 4: Check if the draft deletion is completed successfully                        if (provider.apiResponse.status == Status.COMPLETED) {                          // Reset draftId and navigate back to allow a fresh application submission                          draftId = null;                          _navigationService.goBack();                          // debugPrint("The current date is within the start and end date range.");                        }                      }                      else {                        // Case: Current date is outside the allowed submission period                        // Navigate the user to the initial screen and display a notification message                        _navigationService.goBackUntilFirstScreen();                        _alertService.showCustomSnackBar(                          // "Fresh application submission dates are over. Please try later.",                          localization.submissionformApplicaClosed,                          // context                        );                        // debugPrint("The current date is outside the start and end date range.");                      }                    },);                  },                ),                const SizedBox(height: 10),                /// go with draft                CustomButton(buttonName: localization.yes, isLoading: false, textDirection: textDirection, onTap: (){                  _moveWithDraft(draftData);                })              ],          ),)        );      });      /// prefill the form with the draft      /// _prefillFormWithDraft(draft);    }  }}  }  /// Function to move with draft application and also move back if not fulfill the requirements.  _moveWithDraft(draftData){  /// CHECK FOR THE CONDITION THAT IF DRAFT SUBMISSION DATE IS THERE ONLY THEN SUBMIT THE DRAFT APPLICATION  /// if within submission period then its ok if not then check for draft end date if within draft end date then also  /// move with draft else navigate user back and tell the user that draft submission date is also over.  if(isWithinSubmissionPeriod() || isWithinDraftSubmissionPeriod()){  _initializeTheDraftFields(draftData);  if(widget.draftId == null) {_navigationService.goBack();}  }  else{    if(widget.draftId == null) _navigationService.goBackUntilFirstScreen();    if(widget.draftId != null) _navigationService.goBack();  _alertService.showErrorSnackBar(AppLocalizations.of(context)!.draftDatesOver);  }}  /// function to initialize the data using draft  _initializeTheDraftFields(draftData){    /// clean the draft application data and prefill the fields    Map<String, dynamic> cleanedDraft = jsonDecode(cleanDraftXmlToJson(draftData?.applicationData ?? ''));    final langProvider = Provider.of<LanguageChangeViewModel>(context, listen: false);    if (cleanedDraft['nameAsPasport'] != null) {      _nameAsPassport.clear();      if(cleanedDraft['nameAsPasport'] is List){        for (var ele in cleanedDraft['nameAsPasport']) {          final element = PersonName.fromJson(ele);          if(element.nameTypeController.text == 'PRI'){            _arabicName = element;            _nameAsPassport.add(_arabicName);          }          if(element.nameTypeController.text == 'ENG'){            _englishName = element;            _nameAsPassport.add(_englishName);          }        }      }    }    /// passport Data Prefilled    _passportNationalityController.text = cleanedDraft['country'] ?? '';    _passportPlaceOfIssueController.text = cleanedDraft['passportIssuePlace'] ?? '';    _passportNumberController.text = cleanedDraft['passportId'] ?? '';    _passportIssueDateController.text = formatDateOnly(cleanedDraft['passportIssueDate']?.toString() ?? '');    _passportExpiryDateController.text = formatDateOnly(cleanedDraft['passportExpiryDate']?.toString() ?? '');    _passportUnifiedNoController.text = cleanedDraft['unifiedNo']?.toString() ?? '';    /// personal information prefill    _emiratesIdExpiryDateController.text = formatDateOnly(cleanedDraft['emirateIdExpiryDate']?.toString() ?? '');    _dateOfBirthController.text = formatDateOnly(cleanedDraft['dateOfBirth']?.toString() ?? '');    _placeOfBirthController.text = cleanedDraft['placeOfBirth'] ?? '';    _genderController.text = cleanedDraft['gender'] ?? '';    _maritalStatusController.text = cleanedDraft['maritalStatus'] ?? '';    _studentEmailController.text = cleanedDraft['emailId'] ?? '';    _isMotherUAECheckbox = cleanedDraft["uaeMother"] == 'true'? true : false;    havingSponsor = cleanedDraft["havingSponser"] ?? '';    /// family information    _familyInformationEmiratesController.text = cleanedDraft['familyNo'] ?? '';    _populateTownOnFamilyInformationEmiratesItem(langProvider: langProvider);    _familyInformationTownVillageNoController.text = cleanedDraft['town'] ?? '';    _familyInformationParentGuardianNameController.text = cleanedDraft['parentName'] ?? '';    _familyInformationRelationTypeController.text = cleanedDraft['relationType'] ?? '';    _familyInformationFamilyBookNumberController.text = cleanedDraft['familyNumber'] ?? '';    _familyInformationMotherNameController.text = cleanedDraft['motherName'] ?? '';    /// scholarship relative    _isRelativeStudyingFromScholarship = cleanedDraft['relativeStudyinScholarship'] == 'true';    if ((_isRelativeStudyingFromScholarship ?? false) && cleanedDraft['relativeDetails'] != null && cleanedDraft['relativeDetails'].toString().trim().isNotEmpty) {      _relativeInfoList.clear();      if(cleanedDraft['relativeDetails'] is List)      {        for (var element in cleanedDraft['relativeDetails']) {          _relativeInfoList.add(RelativeInfo.fromJson(element));        }      }      else{        _relativeInfoList.add(RelativeInfo.fromJson(cleanedDraft['relativeDetails']));      }    }    /// contact information    if (cleanedDraft['phoneNunbers'] != null && cleanedDraft['phoneNunbers'].toString().trim().isNotEmpty) {      _phoneNumberList.clear();      for (var element in cleanedDraft['phoneNunbers']) {        _phoneNumberList.add(PhoneNumber.fromJson(element));      }    }    /// address information    if (cleanedDraft['addressList'] != null && cleanedDraft['addressList'].toString().trim().isNotEmpty) {      _addressInformationList.clear(); /// Clear the current list      if (cleanedDraft['addressList'] is List) {        for (int index = 0;        index < cleanedDraft['addressList'].length;        index++) {          var element = cleanedDraft['addressList'][index];          _addressInformationList.add(Address.fromJson(element)); /// Add to the list          _populateStateDropdown(langProvider: langProvider, index: index);        }      } else {        _addressInformationList.add(Address.fromJson(cleanedDraft['addressList'])); /// Add to the list        _populateStateDropdown(langProvider: langProvider, index: 0);      }    }    /// military Services:    _militaryServiceController.text = cleanedDraft['militaryService'] ?? '';    switch (_militaryServiceController.text) {      case 'Y':        _isMilitaryService = MilitaryStatus.yes;      case 'N':        _isMilitaryService = MilitaryStatus.no;      case 'P':        _isMilitaryService = MilitaryStatus.postponed;      case 'R':        _isMilitaryService = MilitaryStatus.exemption;    }    _militaryServiceStartDateController.text =  formatDateOnly(cleanedDraft['militaryServiceStartDate'] ?? '');    _militaryServiceEndDateController.text =  formatDateOnly(cleanedDraft['militaryServiceEndDate'] ?? '');    _reasonForMilitaryController.text = cleanedDraft['reasonForMilitarty'] ?? '';    /// graduation details    if (cleanedDraft['graduationList'] != null &&  cleanedDraft['graduationList'].toString().trim().isNotEmpty) {      _graduationDetailsList.clear(); /// Clear the current list      if(cleanedDraft['graduationList'] is List) {        for (int index = 0; index < cleanedDraft['graduationList'].length; index++) {          var element = cleanedDraft['graduationList'][index];          _graduationDetailsList.add(GraduationInfo.fromJson(element)); /// Add to the list          /// populate dropdowns          _populateGraduationLastTermMenuItemsList(langProvider: langProvider, index: index);          _populateUniversityMenuItemsList(langProvider: langProvider, index: index);        }      }      else{        _graduationDetailsList.add(GraduationInfo.fromJson(cleanedDraft['graduationList'])); /// Add to the list        /// populate dropdowns        _populateGraduationLastTermMenuItemsList(langProvider: langProvider, index: 0);        _populateUniversityMenuItemsList(langProvider: langProvider, index: 0);      }    }    _acadProgramDdsController.text = cleanedDraft['acadProgramDds'] ?? '';    _acadProgramPgrdController.text = cleanedDraft['acadProgramPgrd'] ?? '';    /// major Details    if (cleanedDraft['majorWishList'] != null && cleanedDraft['majorWishList'] != 'true' && cleanedDraft['majorWishList'].toString().trim().isNotEmpty) {      _majorsWishlist.clear(); /// Clear the current list      if(cleanedDraft['majorWishList'] is List){        for (int index = 0;        index < cleanedDraft['majorWishList'].length;        index++) {          var element = cleanedDraft['majorWishList'][index];          _majorsWishlist.add(MajorWishList.fromJson(element)); /// Add to the list        }      }      else{        _majorsWishlist.add(MajorWishList.fromJson(cleanedDraft['majorWishList'])); /// Add to the list      }    }    /// university Priority    if (cleanedDraft['universtiesPriorityList'] != null &&  cleanedDraft['universtiesPriorityList'].toString().trim().isNotEmpty) {      _universityPriorityList.clear(); /// Clear the current list      if( cleanedDraft['universtiesPriorityList'] is List){        for (int index = 0; index < cleanedDraft['universtiesPriorityList'].length; index++) {          var element = cleanedDraft['universtiesPriorityList'][index];          _universityPriorityList.add(UniversityPriority.fromJson(element)); /// Add to the list          populateUniversitiesWishList(_universityPriorityList[index]);        }}      else{        _universityPriorityList.add(UniversityPriority.fromJson(cleanedDraft['universtiesPriorityList']));        populateUniversitiesWishList(_universityPriorityList[0]);      }    }    /// required Examinations    if (cleanedDraft['requiredExaminationList'] != null &&        cleanedDraft['requiredExaminationList'].toString().trim().isNotEmpty) { // Make sure we are checking as a string      _requiredExaminationList.clear(); // Clear the current list      if (cleanedDraft['requiredExaminationList'] is List) {        // If it's a list, iterate through it        for (int index = 0; index < cleanedDraft['requiredExaminationList'].length; index++) {          var element = cleanedDraft['requiredExaminationList'][index];          _requiredExaminationList.add(RequiredExaminations.fromJson(element)); // Add to the list          // populate examination type dropdown          _populateExaminationTypeDropdown(langProvider: langProvider, index: index);        }      } else {        // If it's not a list (presumably a single object), handle it here        _requiredExaminationList.add(RequiredExaminations.fromJson(cleanedDraft['requiredExaminationList'])); // Add to the list        _populateExaminationTypeDropdown(langProvider: langProvider, index: 0);      }    }    /// employment Status    _employmentStatus = cleanedDraft['employmentStatus'] ?? '';    /// Employment History    if (cleanedDraft['emplymentHistory'] != null && cleanedDraft['emplymentHistory'] != 'true' &&  displayEmploymentHistory()) {      _employmentHistoryList.clear(); /// Clear the current list      if (cleanedDraft['emplymentHistory'] is List) {        for (int index = 0;        index < cleanedDraft['emplymentHistory'].length;        index++) {          var element = cleanedDraft['emplymentHistory'][index];          _employmentHistoryList.add(EmploymentHistory.fromJson(              element)); /// Add to the list          /// populate examination type dropdown          _populateExaminationTypeDropdown(            langProvider: langProvider,            index: index,          );        }      } else {        _employmentHistoryList.add(EmploymentHistory.fromJson(            cleanedDraft['emplymentHistory'])); /// Add to the list        /// populate examination type dropdown        _populateExaminationTypeDropdown(          langProvider: langProvider,          index: 0,        );      }    }    /// high school    if (cleanedDraft['highSchoolList'] != null && displayHighSchool()) {      _highSchoolList.clear(); /// Clear the current list      if (cleanedDraft['highSchoolList'] is List) {        for (int index = 0; index < cleanedDraft['highSchoolList'].length; index++) {          var element = cleanedDraft['highSchoolList'][index];          _highSchoolList.add( HighSchool(              hsLevelController: TextEditingController(text: element['hsLevel']),              hsNameController: TextEditingController(text: element['hsName']),              hsCountryController: TextEditingController(text: element['hsCountry']),              hsStateController: TextEditingController(text: element['hsState']),              yearOfPassingController: TextEditingController(text: formatDateOnly(element['yearOfPassing'])),              hsTypeController: TextEditingController(text: element['hsType']),              curriculumTypeController: TextEditingController(text: element['curriculumType']),              curriculumAverageController: TextEditingController(text: element['curriculumAverage']),              otherHsNameController: TextEditingController(text: element['otherHsName'] ),              passingYearController: TextEditingController(text: element['passignYear']),              maxDateController: TextEditingController(text: formatDateOnly(element['maxDate'])),              disableStateController: TextEditingController(text: element['disableState']),              isNewController: TextEditingController(text: element['isNew']),              highestQualificationController: TextEditingController(text: element['highestQualification']),              hsLevelFocusNode: FocusNode(),              hsNameFocusNode: FocusNode(),              hsCountryFocusNode: FocusNode(),              hsStateFocusNode: FocusNode(),              yearOfPassingFocusNode: FocusNode(),              hsTypeFocusNode: FocusNode(),              curriculumTypeFocusNode: FocusNode(),              curriculumAverageFocusNode: FocusNode(),              otherHsNameFocusNode: FocusNode(),              passingYearFocusNode: FocusNode(),              maxDateFocusNode: FocusNode(),              hsDetails: (element['hsDetails'] is List                  ? (element['hsDetails'] as List)                  .map((e) => HSDetails.fromJson(e))                  .toList()                  : []), /// Provide an empty list if hsDetails is not a List              otherHSDetails: (element['otherHSDetails'] is List                  ? (element['otherHSDetails'] as List)                  .map((e) => HSDetails.fromJson(e))                  .toList()                  : []), /// Provide an empty list if otherHSDetails is not a List              schoolStateDropdownMenuItems: [],              schoolNameDropdownMenuItems: [],              schoolTypeDropdownMenuItems: [],              schoolCurriculumTypeDropdownMenuItems: [])); /// Add to the list          _populateHighSchoolStateDropdown(langProvider: langProvider,index: index);          _populateHighSchoolNameDropdown(langProvider: langProvider,index: index);          _populateHighSchoolCurriculumTypeDropdown(langProvider: langProvider,index: index);        }      } else {        _highSchoolList.add(HighSchool.fromJson(cleanedDraft['highSchoolList'])); /// Add to the list        // _populateHighSchoolStateDropdown(langProvider: langProvider,index: 0);        // _populateHighSchoolNameDropdown(langProvider: langProvider,index: 0);        // _populateHighSchoolCurriculumTypeDropdown(langProvider: langProvider,index: 0);      }    }    // print(cleanedDraft['attachments']);    // _myAttachmentsList.clear(); /// Clear the current list    //    // attachments           // if (cleanedDraft['attachments'] != null && cleanedDraft['attachments'] != '' && cleanedDraft['attachments'].toString().trim().isNotEmpty) {           //   _myAttachmentsList.clear(); /// Clear the current list           //   if(cleanedDraft['attachments'] is List){           //   for (int index = 0; index < cleanedDraft['attachments'].length; index++) {           //     var element = cleanedDraft['attachments'][index];           //     _myAttachmentsList.add(Attachment.fromJson(element));           // }}           //   else{           //     _myAttachmentsList.add(Attachment.fromJson(cleanedDraft['attachments']));           //   }           // }    // print("Hello world");    // print("Printing attachment: ${cleanedDraft['attachments']}");    // print(_myAttachmentsList.length);    if (cleanedDraft['attachments'] != null && cleanedDraft['attachments'].toString().trim().isNotEmpty) {      bool hasValidAttachments = true;      if (cleanedDraft['attachments'] is List) {        for (var element in cleanedDraft['attachments']) {          /// Check if processCD or documentCD is empty          if ((element['processCD'] == null || element['processCD'].toString().trim().isEmpty) ||              (element['documentCD'] == null || element['documentCD'].toString().trim().isEmpty)) {            hasValidAttachments = false;            break;          }        }      } else {        var element = cleanedDraft['attachments'];        /// Check if processCD or documentCD is empty for single attachment        if ((element['processCD'] == null || element['processCD'].toString().trim().isEmpty) ||            (element['documentCD'] == null || element['documentCD'].toString().trim().isEmpty)) {          hasValidAttachments = false;        }      }      if (hasValidAttachments) {        /// Clear and add attachments if valid        _myAttachmentsList.clear();        _filteredMyAttachmentsList.clear();        if (cleanedDraft['attachments'] is List) {          for (var element in cleanedDraft['attachments']) {            _myAttachmentsList.add(Attachment.fromJson(element));            _filteredMyAttachmentsList.add(Attachment.fromJson(element));          }        } else {          _myAttachmentsList.add(Attachment.fromJson(cleanedDraft['attachments']));          _filteredMyAttachmentsList.add(Attachment.fromJson(cleanedDraft['attachments']));        }      }    }    // print("Print attachment filted: ${_filteredAttachmentsList.length}");    // _navigationService.goBack();  }  /// *------------------------- Method to check that user has already applied ot not ----------------------------------*  Future<bool> _checkIfAlreadyApplied()async{    final langProvider = Provider.of<LanguageChangeViewModel>(context,listen: false);    final localization = AppLocalizations.of(context)!;    await _getSelectedScholarship(langProvider);    if(_selectedScholarship?.admitType == 'UPP'){      return true;    }    /// Get the list of applications of user    final applicationStatusProvider = Provider.of<GetListApplicationStatusViewModel>(context,listen: false);    await applicationStatusProvider.getListApplicationStatus();    String? assessmentYear;    String? cohort;    if(applicationStatusProvider.apiResponse.status == Status.COMPLETED) {      bool havingItem = applicationStatusProvider.apiResponse.data?.data?.applicationStatus.isNotEmpty ?? false;      if(havingItem){        try{          // ApplicationStatus? appliedApplication = applicationStatusProvider.apiResponse.data?.data?.applicationStatus.firstWhere((element) => element.applicationStatus.programAction != 'DRAFT' && element.applicationStatus.admitType != 'UPP');          final applicationStatuses = applicationStatusProvider.apiResponse.data?.data?.applicationStatus              .where((element) => element.applicationStatus.programAction != 'DRAFT' && element.applicationStatus.admitType != 'UPP')              .toList();          ApplicationStatus? appliedApplication = applicationStatuses?.isNotEmpty == true ? applicationStatuses!.first : null;          if(appliedApplication != null){            final getAppliedApplicationProvider = Provider.of<GetSubmittedApplicationDetailsByApplicationNumberViewModel>(context,listen: false);            await getAppliedApplicationProvider.getSubmittedApplicationDetailsByApplicationNumber(applicationNumber: appliedApplication.applicationStatus.admApplicationNumber);            if(getAppliedApplicationProvider.apiResponse.status == Status.COMPLETED){              assessmentYear = getAppliedApplicationProvider.apiResponse.data?.assesmentYear?.toString();              final GetAllActiveScholarshipsModel? applyingToScholarship =  widget.getAllActiveScholarships?.firstWhere((element)=>element?.configurationKey == widget.selectedScholarshipConfigurationKey);              cohort = applyingToScholarship?.cohort.toString();              // print("Cohort Tag for applying scholarship: ${applyingToScholarship?.cohort.toString()}");              if(assessmentYear!= null && cohort != null){                bool isAlreadyApplied = assessmentYear == cohort ;                if (isAlreadyApplied) {                  _alertService.showErrorSnackBar(localization.alreadyApplied);                  _navigationService.goBack();                  return false;                }                else {                  // User can fill the form if no application other than draft is found                  // debugPrint("You can fill the scholarship form");                  return true;                }              }            }          }          else{            return true;          }        }        catch(error){          /// if we don't have any scholarship other then upp then let the user to fill scholarship form          return true;          // _navigationService.goBack();        }      }      else{        return true;      }    }    // final localization = AppLocalizations.of(context)!;    // final bool isLoggedIn = await _authServices.isLoggedIn();    //    // if(isLoggedIn){    //   // Getting Fresh Roles    //   final getRolesProvider = Provider.of<GetRoleViewModel>(context,listen:false);    //   await getRolesProvider.getRoles();    // }    //    // final UserRole role =  getRoleFromList(HiveManager.getRole());    //    // // Show alert and go back if already applied    // if (alreadyAppliedResult) {    // if (isLoggedIn && (role == UserRole.applicants || role == UserRole.scholarStudent)) {    //   _alertService.showErrorSnackBar(localization.alreadyApplied);    //   _navigationService.goBack();    //   return false;    // }    // else {    //   // User can fill the form if no application other than draft is found    //   // debugPrint("You can fill the scholarship form");    //   return true;    // }    return false;  }  /// *------------------------- Init State of the form start ----------------------------------*  @override  void initState() {    _initializeServices();    WidgetsBinding.instance.addPostFrameCallback((callback)async{      /// initialize the services      _initializeServices();      /// check if user can apply for scholarship or not      /// Check the applications status from application-status api and if any api found else draft then move user back and tell him you already      /// had applied for this year please check your application status and if any issue persists then please contact support.    bool canApply =   await _checkIfAlreadyApplied();    if(canApply){      /// calling initializer to initialize the data      _initializer();    }    });    super.initState();    _initializeServices();  }  /// *------------------------- Init State of the form end -----------------------------------------*  /// boolean to save the state of the modelProgressHud  bool processing = false;  setProcessingMode({required bool status}){    setState(() {      processing = status;    });  }  @override  Widget build(BuildContext context) {    final langProvider = Provider.of<LanguageChangeViewModel>(context, listen: true);final localization = AppLocalizations.of(context)!;    return Scaffold(appBar: CustomSimpleAppBar(            title: Text(localization.apply_for_scholarship, style: AppTextStyles.appBarTitleStyle()),inNotifications: true,),        backgroundColor: Colors.white,        body: Utils.modelProgressHud(          processing: processing,          child: ValueListenableBuilder(              valueListenable: _notifier,              builder: (context, snapshot, child) {                if (snapshot.connectionState == ConnectionState.waiting) {                  return Center(child: Utils.pageLoadingIndicator(context: context));                }                if (snapshot.hasError) {                  return Utils.showOnError();                }                return Stack(                  children: [                    /// main form                    Directionality(                      textDirection: getTextDirection(langProvider),                      child: Column(                        crossAxisAlignment: CrossAxisAlignment.start,                        mainAxisAlignment: MainAxisAlignment.center,                        children: [                          /// title and progress section                          StepsProgressView(                            totalSections: totalSections,                            currentSectionIndex: _currentSectionIndex,                            selectedScholarship: _selectedScholarship,                            scholarshipTitle: _scholarshipTitle,                          ),                          /// Form Sections with PageView                          Expanded(                            child: PageView.builder(                                controller: _pageController,                                physics: const NeverScrollableScrollPhysics(),                                /// Disable swipe gestures                                itemCount: totalSections,                                itemBuilder: (context, index) {                                  String key = _selectedScholarship?.configurationKey ?? '';                                  String academicCareer = _selectedScholarship?.acadmicCareer ?? '';                                  /// Use a helper function to avoid repetition                                  // bool shouldShowHighSchoolDetails() {                                  //   return academicCareer == 'UG' ||                                  //       academicCareer == 'UGRD' ||                                  //       academicCareer == 'SCHL' ||                                  //       academicCareer == 'HCHL';                                  // }                                  // bool isUniversityAndMajorsRequired() {                                  //   return academicCareer != 'SCHL';                                  // }                                  // bool isRequiredExaminationDetailsRequired() {                                  //   return !(academicCareer == 'SCHL' || academicCareer == 'HCHL');                                  // }                                  // bool isAttachmentSectionForExt() {                                  //   return key == 'SCOUPPEXT';                                  // }                                  // bool shouldDisplayEmploymentHistory() {                                  //   return displayEmploymentHistory(); /// Assuming this is a helper function                                  // }                                  /// Switch case handling for index                                  switch (index) {                                  // case 0:                                  //   return _studentUndertakingSection(                                  //       step: index, langProvider: langProvider);                                    case 0:                                      return _studentDetailsSection(                                          step: index, langProvider: langProvider);                                    case 1:                                      return SingleChildScrollView(                                        child: Column(                                          children: [                                            /// High school details section if the condition matches                                            shouldShowHighSchoolDetails(academicCareer)                                                ? _highSchoolDetailsSection(step: index,                                                langProvider: langProvider)                                                : showVoid,                                            /// Graduation details section                                            _graduationDetailsSection(                                                step: index,                                                langProvider: langProvider),                                          ],                                        ),                                      );                                    case 2:                                    /// University and majors section if the condition matches                                      return shouldShowUniversityAndMajors(academicCareer)                                          ? _universityAndMajorsDetailsSection(                                          step: index, langProvider: langProvider)                                          : showVoid;                                    case 3:                                    /// Handle based on scholarship type and configuration key                                      if (shouldShowAttachmentSectionForExt(configurationKey: key)) {                                        return _attachmentsSection(                                            step: index, langProvider: langProvider);                                      }                                      else {                                        return shouldShowRequiredExaminations(academicCareer)                                            ? _requiredExaminationsDetailsSection(                                            step: index, langProvider: langProvider)                                            : showVoid;                                      }                                    case 4:                                    /// Handling based on configuration key for attachments or employment history                                      if (shouldShowAttachmentSectionForExt(configurationKey: key)) {                                        return _confirmation();                                      }                                      else if (key == 'SCOACTUGRD' ||                                          key == "SCOUGRDINT" ||                                          key == 'SCOMETLOGINT' ||                                          key == 'SCOUGRDEXT') {                                        return _attachmentsSection(step: index, langProvider: langProvider);                                      } else {                                        return shouldShowEmploymentHistory(key)                                            ? _employmentHistoryDetailsSection(                                            step: index, langProvider: langProvider)                                            : showVoid;                                      }                                    case 5:                                    /// Display confirmation based on configuration key                                      if (shouldShowAttachmentSectionForExt(configurationKey: key)) {                                        return _studentUndertakingSection(step: index, langProvider: langProvider);                                      }                                      else if (key == 'SCOACTUGRD' ||                                          key == "SCOUGRDINT" ||                                          key == "SCOMETLOGINT" ||                                          key == "SCOUGRDEXT") {                                        return _confirmation();                                      } else {                                        return _attachmentsSection(                                            step: index, langProvider: langProvider);                                      }                                    case 6:                                      if (key == 'SCOACTUGRD' ||                                          key == "SCOUGRDINT" ||                                          key == "SCOMETLOGINT" ||                                          key == "SCOUGRDEXT") {                                        return _studentUndertakingSection(step: index, langProvider: langProvider);                                      }                                      /// Confirmation at the end                                      return _confirmation();                                    case 7:                                      return _studentUndertakingSection(step: index, langProvider: langProvider);                                    default:                                      return Container(); /// Fallback for unexpected index values                                  }                                }),                          ),                        ],                      ),                    ),                    /// Draft Prev and next button                    if(!_applicationSubmitted)  Positioned(                      bottom: 0,                      child: Container(                        padding: EdgeInsets.symmetric(horizontal: kPadding),                        width: screenWidth,                        color: Colors.white,                        child: draftPrevNextButtons(langProvider),                      ),                    ),                  ],                );              })        )        );  }  /// *--------------------------------------------------------------- Accept terms and conditions start ----------------------------------------------------------------------------*  /// step-1: student undertaking  /// student undertaking check:  bool _acceptStudentUndertaking = false;  String? _acceptStudentUndertakingErrorText;  Widget _studentUndertakingSection({required int step,required LanguageChangeViewModel langProvider}){    final arabicFullName = "${_arabicName.studentNameController.text} ${_arabicName.fatherNameController.text} ${_arabicName.grandFatherNameController.text} ${_arabicName.familyNameController.text}";    final englishFullName = "${_englishName.studentNameController.text} ${_englishName.fatherNameController.text} ${_englishName.grandFatherNameController.text} ${_englishName.familyNameController.text}";    return      _applicationSubmitted ?      ApplicationSubmissionConfirmationCard(applicationNumber: _applicationNumber, arabicName: arabicFullName, englishName: englishFullName)          :        StudentUndertakingView(          errorText: _acceptStudentUndertakingErrorText,        acceptStudentUndertaking: _acceptStudentUndertaking,        selectedScholarship: _selectedScholarship,        step: step,        onSubmit: ()async {      var result = await validateSection(step: step, langProvider: langProvider);      if (result) {        // nextSection();        await _submitApplication(langProvider);      }    },        onAcceptTerms: (value) {          setState(() {      