text,                                    controller: relativeInformation                                        .relationTypeController,                                    currentFocusNode: relativeInformation                                        .relationTypeFocusNode,                                    menuItemsList:                                    _relationshipTypeMenuItemsList,                                    hintText: localization.relationTypeWatermark,                                    errorText: relativeInformation                                        .relationTypeError,                                    onChanged: (value) {                                      relativeInformation                                          .relationTypeError = null;                                      setState(() {                                        /// setting the value for relation type                                        relativeInformation                                            .relationTypeController                                            .text = value!;                                        ///This thing is creating error: don't know how to fix it:                                        Utils.requestFocus(                                            focusNode: _relativeInfoList[                                            index]                                                .countryUniversityFocusNode,                                            context: context);                                      });                                    },                                  ),                                  /// ****************************************************************************************************************************************************                                  /// relative country-University                                  kFormHeight,                                  fieldHeading(                                      title: localization.university,                                      important: _isRelativeStudyingFromScholarship ?? false,                                      langProvider: langProvider),                                  scholarshipFormTextField(                                      currentFocusNode: relativeInformation                                          .countryUniversityFocusNode,                                      nextFocusNode: index <                                          _relativeInfoList.length - 1                                          ? _relativeInfoList[index + 1]                                          .relativeNameFocusNode                                          : null,                                      controller: relativeInformation                                          .countryUniversityController,                                      hintText: localization.universityWatermark,                                      errorText: relativeInformation                                          .countryUniversityError,                                      onChanged: (value) {                                        /// no validation has been provided                                        if (relativeInformation                                            .countryUniversityFocusNode                                            .hasFocus) {                                          setState(() {                                            relativeInformation                                                .countryUniversityError =                                                ErrorText.getEmptyFieldError(                                                    name: _relativeInfoList[                                                    index]                                                        .countryUniversityController                                                        .text,                                                    context: context);                                          });                                        }                                      }),                                  /// ****************************************************************************************************************************************************                                  /// relative country-University                                  /// kFormHeight,                                  /// fieldHeading(                                  ///     title: "Family Book Number",                                  ///     important: true,                                  ///     langProvider: langProvider),                                  /// scholarshipFormTextField(                                  ///     currentFocusNode: relativeInformation                                  ///         .familyBookNumberFocusNode,                                  ///     nextFocusNode: index <                                  ///             _relativeInfoList.length - 1                                  ///         ? _relativeInfoList[index + 1]                                  ///             .relativeNameFocusNode                                  ///         : null,                                  ///     controller: relativeInformation                                  ///         .familyBookNumberController,                                  ///     errorText: relativeInformation                                  ///         .familyBookNumberError,                                  ///     hintText: "Enter Family Book Number",                                  ///     onChanged: (value) {                                  ///       if (relativeInformation                                  ///           .familyBookNumberFocusNode                                  ///           .hasFocus) {                                  ///         setState(() {                                  ///           relativeInformation                                  ///                   .familyBookNumberError =                                  ///               ErrorText.getEnglishArabicNumberError(                                  ///                   input: _relativeInfoList[                                  ///                           index]                                  ///                       .familyBookNumberController                                  ///                       .text,                                  ///                   context: context);                                  ///         });                                  ///       }                                  ///     }),                                  /// ****************************************************************************************************************************************************                                  /// remove Relative                                  addRemoveMoreSection(                                      title: localization.deleteRowRelativeInfo,                                      add: false,                                      onChanged: () {                                        /// if we remove 0'th index item also then set _isRelativeStudying to false                                        if (_relativeInfoList.length == 1 &&                                            index == 0) {                                          _removeRelative(index);                                          _isRelativeStudyingFromScholarship =                                          false;                                        } else {                                          _removeRelative(index);                                        }                                      }),                                  /// *******************************************