       /// *--------------------------------------------------------- Military Services  Section end ------------------------------------------------------------------------------*/                  ],                )),            const SizedBox(height: 100,),          ],        ),      ),    );  }  /// *--------------------------------------------------------------- Phone Number Information data start----------------------------------------------------------------------------*  /// phone number dropdown menu items list  List<DropdownMenuItem> _phoneNumberTypeMenuItemsList = [];  /// List of Phone Number information  final List<PhoneNumber> _phoneNumberList = [];  /// Method to add a new phone number to the contact information list  void _addPhoneNumber() {    setState(() {      _phoneNumberList.add(PhoneNumber(          countryCodeController: TextEditingController(),          phoneNumberController: TextEditingController(),          phoneTypeController: TextEditingController(),          preferred: false,          isExisting: false,          countryCodeFocusNode: FocusNode(),          phoneNumberFocusNode: FocusNode(),          phoneTypeFocusNode: FocusNode(),          phoneNumberError: null,          phoneTypeError: null,          countryCodeError: null));    });  }  /// Method to add a new phone number to the contact information list  void _removePhoneNumber(int index) {    if (index >= 2 && index < _phoneNumberList.length) {      /// Check if index is valid      setState(() {        final phoneNumber = _phoneNumberList[index];        phoneNumber.countryCodeController.dispose(); /// Dispose the controllers        phoneNumber.phoneNumberController.dispose();        phoneNumber.phoneTypeController.dispose();        phoneNumber.preferred = false;        phoneNumber.countryCodeFocusNode.dispose(); /// Dispose the controllers        phoneNumber.phoneNumberFocusNode.dispose();        phoneNumber.phoneTypeFocusNode.dispose();        _phoneNumberList.removeAt(index);      });    } else {      // print("Invalid index: $index"); /// Debugging print to show invalid index    }  }  Widget _phoneNumberSection() {    /// defining langProvider    final langProvider = Provider.of<LanguageChangeViewModel>(context, listen: false);    final localization = AppLocalizations.of(context)!;    return Column(      children: [        ListView.builder(            shrinkWrap: true,            padding: EdgeInsets.zero,            physics: const NeverScrollableScrollPhysics(),            itemCount: _phoneNumberList.length,            itemBuilder: (context, index) {              final phoneNumber = _phoneNumberList[index];              return Column(                mainAxisAlignment: MainAxisAlignment.center,                crossAxisAlignment: CrossAxisAlignment.end,                mainAxisSize: MainAxisSize.min,                children: [                  /// phone Type                  fieldHeading(                      title: localization.submissionPhoneType,                      important: true,                      langProvider: langProvider,                  ),                  scholarshipFormDropdown(context:context,                    readOnly: (index == 0 || index == 1),                    // filled: (index == 0 || index == 1),                    filled: true,                    fillColor: (index == 0 || index == 1) ? AppColors.lightGrey : Colors.white,                    controller: phoneNumber.phoneTypeController,                    currentFocusNode: phoneNumber.phoneTypeFocusNode,                    menuItemsList: _phoneNumberTypeMenuItemsList,                    hintText: localization.select,                    errorText: phoneNumber.phoneTypeError,                    onChanged: (value) {                      setState(() {                        phoneNumber.phoneTypeError = null;                        final isDuplicate = _phoneNumberList.any((element){                          return element != phoneNumber && element.phoneTypeController.text == value;                        });                        if(isDuplicate){                          phoneNumber.phoneTypeError = "Phone Type already exists";                        }                        else                        {                          /// setting the value for relation type                          phoneNumber.phoneTypeController.text = value!;                          ///This thing is creating error: don't know how to fix it:                          Utils.requestFocus(                              focusNode: phoneNumber.phoneNumberFocusNode,                              context: context);                        }                      });                    },                    // {                    //   phoneNumber.phoneTypeError = null;                    //   setState(() {                    //     /// setting the value for relation type                    //     phoneNumber.phoneTypeController.text = value!;                    //     ///This thing is creating error: don't know how to fix it:                    //     Utils.requestFocus(                    //         focusNode: phoneNumber.phoneNumberFocusNode,                    //         context: context);                    //   });                    // },                  ),                  /// ****************************************************************************************************************************************************                  kFormHeight,                  fieldHeading(                      title: localization.homeNumber,                      important: true,                      langProvider: langProvider),                  scholarshipFormTextField(                      currentFocusNode: phoneNumber.phoneNumberFocusNode,                      nextFocusNode: phoneNumber.countryCodeFocusNode,                      controller: phoneNumber.phoneNumberController,                      hintText: localization.homeNumberWatermark,                      errorText: phoneNumber.phoneNumberError,                      onChanged: (value) {                        /// no validation has been provided                        if (phoneNumber.phoneNumberFocusNode.hasFocus) {                          setState(() {                            final isDuplicate = _phoneNumberList.any((element){                              return element!= phoneNumber && element.phoneNumberController.text == value;                            });                            if(isDuplicate){                              phoneNumber.phoneNumberError = localization.phoneNumberAlreadyEntered;                            }                            else{                              phoneNumber.phoneNumberController.text = value!;                              phoneNumber.phoneNumberError = ErrorText.getPhoneNumberError(phoneNumber: phoneNumber.phoneNumberController.text, context: context);                            }                          });                        }                      }                      // {                      //   /// no validation has been provided                      //   if (phoneNumber.phoneNumberFocusNode.hasFocus) {                      //     setState(() {                      //       phoneNumber.phoneNumberError =                      //           ErrorText.getPhoneNumberError(                      //               phoneNumber:                      //                   phoneNumber.phoneNumberController.text,                      //               context: context);                      //     });                      //   }                      // }                      ),                  /// ****************************************************************************************************************************************************                  kFormHeight,                  fieldHeading(                      title: localization.submissionCountryCode,                      important: true,                      langProvider: langProvider),                  scholarshipFormTextField(                      currentFocusNode: phoneNumber.countryCodeFocusNode,                      nextFocusNode: index < _phoneNumberList.length - 1                          ? _phoneNumberList[index + 1].phoneTypeFocusNode                          : null,                      controller: phoneNumber.countryCodeController,                      hintText: localization.submissionCountryCode,                      errorText: phoneNumber.countryCodeError,                      onChanged: (value) {                        /// no validation has been provided                        if (phoneNumber.countryCodeFocusNode.hasFocus) {                          setState(() {                            phoneNumber.countryCodeError =                                ErrorText.getEmptyFieldError(                                    name:                                        phoneNumber.countryCodeController.text,                                    context: context);                          });                        }                      }),                  /// ****************************************************************************************************************************************************                  kFormHeight,                  /// Preferred                  CustomGFCheckbox(                      value: phoneNumber.preferred,                      onChanged: (onChanged) {                        setState(() {                          for (var element in _phoneNumberList) {                          element.preferred = false;                        }                          phoneNumber.preferred = !phoneNumber.preferred;                        });                      },                      text: localization.submissionPreferred,),                  /// ****************************************************************************************************************************************************                  /// space based on condition                  (index == 0 || index == 1) ? kFormHeight : showVoid,                  /// Add More Information container                  (_phoneNumberList.isNotEmpty && (index != 0 && index != 1))                      ? addRemoveMoreSection(                          title: localization.deleteRowPhone,                          add: false,                          onChanged: () {                            _removePhoneNumber(index);                          })                      : showVoid,                  // const MyDivider(                  //   color: AppColors.lightGrey,                  // ),                 const  Divider(),                  /// ****************************************************************************************************************************************************                  /// space based on if not last item                  index != _phoneNumberList.length - 1 ? kFormHeight : showVoid,                ],              );            }),        /// Add more Phones Numbers        /// Add More Information container        _phoneNumberList.isNotEmpty            ? addRemoveMoreSection(                title: localization.addRowPhone,                add: true,                onChanged: () {                  _addPhoneNumber();                })            : showVoid,      ],    );  }  /// *---------------------------------------------------------------Phone Number Information data end----------------------------------------------------------------------------*  /// *--------------------------------------------------------------- Address Information Section start ----------------------------------------------------------------------------*  /// address type dropdown menu Item list  List<DropdownMenuItem> _addressTypeMenuItemsList = [];  /// populate state dropdown menuItem List  _populateStateDropdown(      {required LanguageChangeViewModel langProvider, required int index}) {    setState(() {      if (Constants              .lovCodeMap[                  'STATE#${_addressInformationList[index].countryController.text}']              ?.values !=          null) {        _addressInformationList[index].stateDropdownMenuItems =            populateCommonDataDropdown(                menuItemsList: Constants                    .lovCodeMap[                        'STATE#${_addressInformationList[index].countryController.text}']!                    .values!,                provider: langProvider,                textColor: AppColors.scoButtonColor);      }    });  }  /// address list  final List<Address> _addressInformationList = [];  /// add Address  void _addAddress() {    setState(() {      _addressInformationList.add(Address(          addressTypeController: TextEditingController(),          addressLine1Controller: TextEditingController(),          addressLine2Controller: TextEditingController(),          /// Optional          cityController: TextEditingController(),          stateController: TextEditingController(),          /// Optional          postalCodeController: TextEditingController(),          /// Optional          countryController: TextEditingController(),          addressTypeFocusNode: FocusNode(),          addressLine1FocusNode: FocusNode(),          addressLine2FocusNode: FocusNode(),          /// Optional          cityFocusNode: FocusNode(),          stateFocusNode: FocusNode(),          /// Optional          postalCodeFocusNode: FocusNode(),          /// Optional          countryFocusNode: FocusNode(),          countryDropdownMenuItems: _nationalityMenuItemsList,          stateDropdownMenuItems: []));    });  }  /// remove address  void _removeAddress(int index) {    if (index >= 1 && index < _addressInformationList.length) {      setState(() {        final addressInformation = _addressInformationList[index];        /// Dispose controllers and focus nodes        addressInformation.addressTypeController.dispose();        addressInformation.addressLine1Controller.dispose();        addressInformation.addressLine2Controller.dispose(); /// Optional        addressInformation.cityController.dispose();        addressInformation.stateController.dispose(); /// Optional        addressInformation.postalCodeController.dispose(); /// Optional        addressInformation.countryController.dispose();        addressInformation.addressTypeFocusNode.dispose();        addressInformation.addressLine1FocusNode.dispose();        addressInformation.addressLine2FocusNode.dispose(); /// Optional        addressInformation.cityFocusNode.dispose();        addressInformation.stateFocusNode.dispose(); /// Optional        addressInformation.postalCodeFocusNode.dispose(); /// Optional        addressInformation.countryFocusNode.dispose();        addressInformation.countryDropdownMenuItems?.clear();        addressInformation.stateDropdownMenuItems?.clear();        /// Remove the address entry from the list        _addressInformationList.removeAt(index);      });    } else {      // print("Invalid index: $index"); /// For debugging invalid index    }  }  Widget _addressInformationSection() {    final localization = AppLocalizations.of(context)!;    /// defining langProvider    final langProvider =        Provider.of<LanguageChangeViewModel>(context, listen: false);    return Column(      children: [        ListView.builder(            shrinkWrap: true,            padding: EdgeInsets.zero,            physics: const NeverScrollableScrollPhysics(),            itemCount: _addressInformationList.length,            itemBuilder: (context, index) {              final addressInformation = _addressInformationList[index];              return Column(                mainAxisAlignment: MainAxisAlignment.center,                crossAxisAlignment: CrossAxisAlignment.end,                mainAxisSize: MainAxisSize.min,                children: [                  /// phone Type                  fieldHeading(                      title: localization.addressType,                      important: true,                      langProvider: langProvider),                  scholarshipFormDropdown(context:context,                    controller: addressInformation.addressTypeController,                    currentFocusNode: addressInformation.addressTypeFocusNode,                    menuItemsList: _addressTypeMenuItemsList,                    hintText: localization.addressTypeWatermark,                    errorText: addressInformation.addressTypeError,                    onChanged: (value) {                      addressInformation.addressTypeError = null;                      final bool isDuplicate = _addressInformationList.any((element){                        return addressInformation != element && element.addressTypeController.text == value;                      });                      if(isDuplicate){                        addressInformation.addressTypeError = localization.addresstypeDuplicate;                      }                      /// setting the value for address type                      addressInformation.addressTypeController.text = value!;                      ///This thing is creating error: don't know how to fix it:                      Utils.requestFocus(                          focusNode: addressInformation.addressLine1FocusNode,                          context: context);                    },                  ),                  /// ****************************************************************************************************************************************************                  kFormHeight,                  fieldHeading(                      title: localization.addressLine1,                      important: true,                      langProvider: langProvider),                  scholarshipFormTextField(                      currentFocusNode:                          addressInformation.addressLine1FocusNode,                      nextFocusNode: addressInformation.addressLine2FocusNode,                      controller: addressInformation.addressLine1Controller,                      hintText: localization.addressLine1Watermark,                      errorText: addressInformation.addressLine1Error,                      onChanged: (value) {                        if (addressInformation.addressLine1FocusNode.hasFocus) {                          setState(() {                            addressInformation.addressLine1Error =                                ErrorText.getEmptyFieldError(                                    name: addressInformation                                        .addressLine1Controller.text,                                    context: context);                          });                        }                      }),                  /// ****************************************************************************************************************************************************                  kFormHeight,                  fieldHeading(                      title: localization.addressLine2,                      important: false,                      langProvider: langProvider),                  scholarshipFormTextField(                      currentFocusNode:                          addressInformation.addressLine2FocusNode,                      nextFocusNode: addressInformation.countryFocusNode,                      controller: addressInformation.addressLine2Controller,                      hintText: localization.addressLine1Watermark,                      errorText: addressInformation.addressLine2Error,                      onChanged: (value) {                        if (addressInformation.addressLine2FocusNode.hasFocus) {                          setState(() {                            /// don't need any type of validation                            // addressInformation.addressLine2Error =                            //     ErrorText.getNameArabicEnglishValidationError(                            //         name: addressInformation                            //             .addressLine2Controller.text,                            //         context: context);                          });                        }                      }),                  /// ****************************************************************************************************************************************************                  kFormHeight,                  /// phone Type                  fieldHeading(                      title: localization.country,                      important: true,                      langProvider: langProvider),                  scholarshipFormDropdown(context:context,                    controller: addressInformation.countryController,                    currentFocusNode: addressInformation.countryFocusNode,                    menuItemsList: _nationalityMenuItemsList,                    hintText: localization.countryWatermark,                    errorText: addressInformation.countryError,                    onChanged: (value) {                      addressInformation.countryError = null;                      setState(() {                        /// setting the value for address type                        addressInformation.countryController.text = value!;                        /// populating the state dropdown                        addressInformation.stateDropdownMenuItems?.clear();                        addressInformation.stateController.clear();                        _populateStateDropdown(langProvider: langProvider, index: index);                        addressInformation.stateDropdownMenuItems?.isEmpty ?? false ? addressInformation.disableState = true : addressInformation.disableState = false;                        ///This thing is creating error: don't know how to fix it:                        Utils.requestFocus(                            focusNode: addressInformation.stateFocusNode,                            context: context);                      });                    },                  ),                  /// ****************************************************************************************************************************************************                  kFormHeight,                  /// phone Type                  fieldHeading(                      title: localization.emirates,                      important: addressInformation.stateDropdownMenuItems?.isNotEmpty ?? false,                      langProvider: langProvider),                  scholarshipFormDropdown(context:context,                    filled: addressInformation.stateDropdownMenuItems?.isEmpty,                    fillColor: (addressInformation.stateDropdownMenuItems?.isEmpty ?? false) ? AppColors.lightGrey : Colors.white,                    controller: addressInformation.stateController,                    currentFocusNode: addressInformation.stateFocusNode,                    menuItemsList: addressInformation.stateDropdownMenuItems,                    hintText: localization.emiratesWatermark,                    errorText: addressInformation.stateError,                    onChanged: (value) {                      addressInformation.stateError = null;                      setState(() {                        /// setting the value for address type                        addressInformation.stateController.text = value!;                        ///This thing is creating error: don't know how to fix it:                        Utils.requestFocus(                            focusNode: addressInformation.cityFocusNode,                            context: context);                      });                    },                  ),                  /// ****************************************************************************************************************************************************                  kFormHeight,                  fieldHeading(                      title: localization.city,                      important: true,                      langProvider: langProvider),                  scholarshipFormTextField(                      currentFocusNode: addressInformation.cityFocusNode,                      nextFocusNode: addressInformation.postalCodeFocusNode,                      controller: addressInformation.cityController,                      hintText: localization.cityWatermark,                      errorText: addressInformation.cityError,                      onChanged: (value) {                        if (addressInformation.cityFocusNode.hasFocus) {                          setState(() {                            addressInformation.cityError =                                ErrorText.getNameArabicEnglishValidationError(                                    name:                                        addressInformation.cityController.text,                                    context: context);                          });                        }                      }),                  /// ****************************************************************************************************************************************************                  kFormHeight,                  fieldHeading(                      title: localization.poBox,                      important: false,                      langProvider: langProvider),                  scholarshipFormTextField(                      currentFocusNode: addressInformation.postalCodeFocusNode,                      controller: addressInformation.postalCodeController,                      hintText: localization.poboxWatermark,                      errorText: addressInformation.postalCodeError,                      onChanged: (value) {                        if (addressInformation.postalCodeFocusNode.hasFocus) {                          setState(() {                            addressInformation.postalCodeError =                                ErrorText.getPinCodeValidationError(                                    pinCode: addressInformation                                        .postalCodeController.text,                                    context: context);                          });                        }                      }),                  /// ****************************************************************************************************************************************************                  /// space based on condition                  (index == 0) ? kFormHeight : showVoid,                  /// Add More Information container                  (_addressInformationList.isNotEmpty && (index != 0))                      ? addRemoveMoreSection(                          title: localization.deleteRowAddress,                          add: false,                          onChanged: () {                            _removeAddress(index);                          })                      : showVoid,                  /// light color divider                  // const MyDivider(                  //   color: AppColors.lightGrey,                  // ),                  const Divider(),                  /// ****************************************************************************************************************************************************                  /// space based on if not last item                  index != _addressInformationList.length - 1                      ? kFormHeight                      : showVoid,                ],              );            }),        /// Add more Phones Numbers        /// Add More Information container        _addressInformationList.isNotEmpty            ? addRemoveMoreSection(                title: localization.addRowAddress,                add: true,                onChanged: () {                  _addAddress();                })            : showVoid,      ],    );  }  /// *--------------------------------------------------------------- Address Information Section end ----------------------------------------------------------------------------*  /// *--------------------------------------------------------------- Military Information Section start ----------------------------------------------------------------------------*  dynamic _isMilitaryService;  /// TextEditingControllers for military service  final TextEditingController _militaryServiceController = TextEditingController();  final TextEditingController _militaryServiceStartDateController = TextEditingController();  final TextEditingController _militaryServiceEndDateController = TextEditingController();  final TextEditingController _reasonForMilitaryController = TextEditingController();  /// FocusNodes for each field  final FocusNode _militaryServiceFocusNode = FocusNode();  final FocusNode _militaryServiceStartDateFocusNode = FocusNode();  final FocusNode _militaryServiceEndDateFocusNode = FocusNode();  final FocusNode _reasonForMilitaryFocusNode = FocusNode();  /// Error texts for each field  String? _selectMilitaryServiceStatusErrorText;  String? _militaryServiceStartDateErrorText;  String? _militaryServiceEndDateErrorText;  String? _reasonForMilitaryErrorText;  /// Show round radiobuttons to select the military options  Widget _militaryServicesSection() {    final localization = AppLocalizations.of(context)!;    final langProvider = Provider.of<LanguageChangeViewModel>(context, listen: false);    return Column(      crossAxisAlignment: CrossAxisAlignment.start,      children: [        /// heading text for user reading        fieldHeading(            title: localization.militaryService,            important: true,            langProvider: langProvider),        /// ****************************************************************************************************************************************************        /// Yes or no : Show round checkboxes to select the military options        CustomRadioListTile(          value: MilitaryStatus.yes,          groupValue: _isMilitaryService,          onChanged: (value) {            setState(() {              _isMilitaryService = value;              _militaryServiceController.text = 'Y';              _selectMilitaryServiceStatusErrorText = null;            });          },          title: localization.yes,          textStyle: textFieldTextStyle,        ),        /// ****************************************************************************************************************************************************        CustomRadioListTile(            value: MilitaryStatus.no,            groupValue: _isMilitaryService,            onChanged: (value) {              setState(() {                _isMilitaryService = value;                /// if no is selected then clear the values in the text editing controllers                _militaryServiceController.text = 'N';                _militaryServiceStartDateController.clear();                _militaryServiceEndDateController.clear();                _reasonForMilitaryController.clear();                _selectMilitaryServiceStatusErrorText = null;              });            },            title: localization.no,            textStyle: textFieldTextStyle),        /// ****************************************************************************************************************************************************        CustomRadioListTile(            value: MilitaryStatus.postponed,            groupValue: _isMilitaryService,            onChanged: (value) {              setState(() {                _isMilitaryService = value;                _militaryServiceController.text = 'P';                _militaryServiceStartDateController.clear();                _militaryServiceEndDateController.clear();                _reasonForMilitaryController.clear();                _selectMilitaryServiceStatusErrorText = null;              });            },            title: localization.postpond,            textStyle: textFieldTextStyle),        /// ****************************************************************************************************************************************************        CustomRadioListTile(            value: MilitaryStatus.exemption,            groupValue: _isMilitaryService,            onChanged: (value) {              setState(() {                _isMilitaryService = value;                _militaryServiceController.text = 'R';                _militaryServiceStartDateController.clear();                _militaryServiceEndDateController.clear();                _reasonForMilitaryController.clear();                _selectMilitaryServiceStatusErrorText = null;              });            },            title: localization.relief,            textStyle: textFieldTextStyle),        /// ****************************************************************************************************************************************************        _militaryServicesFields(),        /// ****************************************************************************************************************************************************        showErrorText(_selectMilitaryServiceStatusErrorText),      ],    );  }  /// input fields based on selection on military services  Widget _militaryServicesFields() {    final localization = AppLocalizations.of(context)!;    final langProvider = Provider.of<LanguageChangeViewModel>(context, listen: false);    switch (_isMilitaryService) {      case MilitaryStatus.yes:        return Column(          children: [            kFormHeight,            ///  Start Date            fieldHeading(                title: localization.militaryServiceStartDate,                important: true,                langProvider: langProvider),            scholarshipFormDateField(              currentFocusNode: _militaryServiceStartDateFocusNode,              controller: _militaryServiceStartDateController,              hintText: localization.militaryServiceStartDateWatermark,              errorText: _militaryServiceStartDateErrorText,              onChanged: (value) async {                setState(() {                  if (_militaryServiceStartDateFocusNode.hasFocus) {                    _militaryServiceStartDateErrorText =                        ErrorText.getEmptyFieldError(                            name: _militaryServiceStartDateController.text,                            context: context);                  }                });              },              onTap: () async {                /// Clear the error if a date is selected                _militaryServiceStartDateErrorText = null;                /// Define the initial date (e.g., today's date)                final DateTime initialDate = DateTime.now();                /// Define the start date (20 years ago from today)                final DateTime firstDate = DateTime.now().subtract(const Duration(days: 20 * 365));                /// Define the last date (today's date)                final DateTime lastDate = DateTime.now();                DateTime? date = await showDatePicker(                  context: context,                  barrierColor: AppColors.scoButtonColor.withOpacity(0.1),                  barrierDismissible: false,                  locale: Provider.of<LanguageChangeViewModel>(context, listen: false).appLocale,                  initialDate: initialDate,                  firstDate: firstDate,                  lastDate: lastDate,                );                if (date != null) {                  setState(() {                    Utils.requestFocus(                        focusNode: _militaryServiceEndDateFocusNode,                        context: context);                    _militaryServiceStartDateController.text =                        DateFormat('yyyy-MM-dd').format(date).toString();                  });                }              },            ),            /// ****************************************************************************************************************************************************            kFormHeight,            ///  End Date            fieldHeading(title: localization.militaryServiceEndDate, important: true, langProvider: langProvider),            scholarshipFormDateField(              currentFocusNode: _militaryServiceEndDateFocusNode,              controller: _militaryServiceEndDateController,              hintText: localization.militaryServiceEndDateWatermark,              errorText: _militaryServiceEndDateErrorText,              onChanged: (value) async {                setState(() {                  if (_militaryServiceEndDateFocusNode.hasFocus) {                    _militaryServiceEndDateErrorText =                        ErrorText.getEmptyFieldError(                      name: _militaryServiceEndDateController.text,                      context: context,                    );                  }                });              },              onTap: () async {                /// Clear the error if a date is selected                _militaryServiceEndDateErrorText = null;                /// Define the initial date (e.g., today's date)                final DateTime initialDate = DateTime.now();                /// Define the start date (10 years ago from today)                final DateTime firstDate = DateTime.now().subtract(const Duration(days: 10 * 365));                /// Define the last date (10 years in the future from today)                final DateTime lastDate = DateTime.now().add(const Duration(days: 10 * 365));                DateTime? date = await showDatePicker(                  context: context,                  barrierColor: AppColors.scoButtonColor.withOpacity(0.1),                  barrierDismissible: false,                  locale: Provider.of<LanguageChangeViewModel>(context, listen: false).appLocale,                  initialDate: initialDate,                  firstDate: firstDate,                  lastDate: lastDate,                );                if (date != null) {                  setState(() {                    _militaryServiceEndDateController.text =                        DateFormat('yyyy-MM-dd').format(date).toString();                    /// Optionally, request focus on the next field                  });                }              },            ),          ],        );      case MilitaryStatus.no:        return showVoid;      case MilitaryStatus.postponed:        return _reason(langProvider);      case MilitaryStatus.exemption:        return _reason(langProvider);      case null:        return showVoid;    }    return showVoid;  }  /// reason widget which is used as common for both relief and exemption  Widget _reason(langProvider) {    final localization = AppLocalizations.of(context)!;    return Column(      children: [        /// ****************************************************************************************************************************************************        kFormHeight,        fieldHeading(            title: localization.militaryReason, important: true, langProvider: langProvider),        scholarshipFormTextField(            currentFocusNode: _reasonForMilitaryFocusNode,            controller: _reasonForMilitaryController,            maxLines: 3,            hintText: localization.militaryReasonWatermark,            errorText: _reasonForMilitaryErrorText,            onChanged: (value) {              if (_reasonForMilitaryFocusNode.hasFocus) {                setState(() {                  _reasonForMilitaryErrorText =                      ErrorText.getNameArabicEnglishValidationError(                          name: _reasonForMilitaryController.text,                          context: context);                });              }            }),      ],    );  }  /// *--------------------------------------------------------------- Military Information Section end ----------------------------------------------------------------------------*  /// ****************************************************************************************************************************************************        /// ****************************************************************************************************************************************************  /// *--------------------------------------------------------------- High School Section Start ----------------------------------------------------------------------------*  /// step-3: high school details    bool displayHighSchool(){    /// We will show High school details option to UG,UGRD ,SCHL and HCHL scholarship types    final academicCareer = _selectedScholarship?.acadmicCareer;    return (academicCareer == 'UG' ||        academicCareer == 'UGRD' ||        academicCareer == 'SCHL' ||        academicCareer == 'HCHL');  }  /// These are dropdown menus  List _highSchoolLevelMenuItemsList = [];  List _highSchoolTypeMenuItemsList = [];  List _highSchoolSubjectsItemsList = [];  /// final HIGH SCHOOL list final List<HighSchool> _highSchoolList = [];  /// to populate the states based on high school country  _populateHighSchoolStateDropdown(      {required LanguageChangeViewModel langProvider, required int index}) {    setState(() {      if (Constants              .lovCodeMap[                  'STATE#${_highSchoolList[index].hsCountryController.text}']              ?.values !=          null) {        _highSchoolList[index].schoolStateDropdownMenuItems =            populateCommonDataDropdown(                menuItemsList: Constants                    .lovCodeMap[                        'STATE#${_highSchoolList[index].hsCountryController.text}']!                    .values!,                provider: langProvider,                textColor: AppColors.scoButtonColor);      }    });  }  _populateHighSchoolNameDropdown(      {required LanguageChangeViewModel langProvider, required int index}) {    setState(() {      if (Constants              .lovCodeMap[                  'SCHOOL_CD#${_highSchoolList[index].hsStateController.text}']              ?.values !=          null) {        _highSchoolList[index].schoolNameDropdownMenuItems =            populateCommonDataDropdown(                menuItemsList: Constants                    .lovCodeMap[                        'SCHOOL_CD#${_highSchoolList[index].hsStateController.text}']!                    .values!,                provider: langProvider,                textColor: AppColors.scoButtonColor);      }    });  }  _populateHighSchoolCurriculumTypeDropdown(      {required LanguageChangeViewModel langProvider, required int index}) {    setState(() {      if (Constants              .lovCodeMap[                  'CURRICULM_TYPE#${_highSchoolList[index].hsTypeController.text}']              ?.values !=          null) {        _highSchoolList[index].schoolCurriculumTypeDropdownMenuItems =            populateCommonDataDropdown(                menuItemsList: Constants                    .lovCodeMap[                        'CURRICULM_TYPE#${_highSchoolList[index].hsTypeController.text}']!                    .values!,                provider: langProvider,                textColor: AppColors.scoButtonColor);      }    });  }  Widget _highSchoolDetailsSection({required int step, required LanguageChangeViewModel langProvider}) {    return HighSchoolView(highSchoolList: _highSchoolList, admitType: _selectedScholarship!.admitType, academicCareer: _selectedScholarship!.acadmicCareer, draftPrevNextButtons: draftPrevNextButtons(langProvider));  }  /// *--------------------------------------------------------------- High School Section end ----------------------------------------------------------------------------*  /// *--------------------------------------------------------------- Graduation Details Section start ----------------------------------------------------------------------------*  final List<GraduationInfo> _graduationDetailsList = [];  List<DropdownMenuItem> _graduationLevelMenuItems = [];  List<DropdownMenuItem> _graduationLevelDDSMenuItems = [];  List<DropdownMenuItem> _caseStudyYearDropdownMenuItems = [];  /// sponsorship question for dds (One student can have only have one sponsor)  String havingSponsor = '';  String? _selectSponsorshipErrorText;  void updateHavingSponsor(String value) {    setState(() {      havingSponsor = value;      _selectSponsorshipErrorText = null;    });  }  /// to populate the graduation Details  _populateGraduationLastTermMenuItemsList({required LanguageChangeViewModel langProvider, required int index}) {    setState(() {      if (Constants.lovCodeMap['LAST_TERM']?.values != null) {        _graduationDetailsList[index].lastTerm = populateCommonDataDropdown(            menuItemsList: Constants.lovCodeMap['LAST_TERM']!.values!,            provider: langProvider,            textColor: AppColors.scoButtonColor);      }    });  }  /// populate menu items list  _populateUniversityMenuItemsList(      {required LanguageChangeViewModel langProvider, required int index})  {    setState(() {      if (Constants              .lovCodeMap[                  'GRAD_UNIVERSITY#${_graduationDetailsList[index].countryController.text}#UNV']              ?.values !=          null) {        _graduationDetailsList[index].university = populateCommonDataDropdown(            menuItemsList: Constants                .lovCodeMap[                    'GRAD_UNIVERSITY#${_graduationDetailsList[index].countryController.text}#UNV']!                .values!,            provider: langProvider,            textColor: AppColors.scoButtonColor);      }    });  }  _addGraduationDetail() {    // bool isAlreadyCurrentlyStudying = _graduationDetailsList.any((element) => element.currentlyStudying == true);    setState(() {      _graduationDetailsList.add(GraduationInfo(        levelController: TextEditingController(),        countryController: TextEditingController(),        universityController: TextEditingController(),        majorController: TextEditingController(),        cgpaController: TextEditingController(),        graduationStartDateController: TextEditingController(),        lastTermController: TextEditingController(),        caseStudyTitleController: TextEditingController(),        caseStudyDescriptionController: TextEditingController(),        caseStudyStartYearController: TextEditingController(),        levelFocusNode: FocusNode(),        countryFocusNode: FocusNode(),        universityFocusNode: FocusNode(),        majorFocusNode: FocusNode(),        cgpaFocusNode: FocusNode(),        graduationStartDateFocusNode: FocusNode(),        lastTermFocusNode: FocusNode(),        caseStudyTitleFocusNode: FocusNode(),        caseStudyDescriptionFocusNode: FocusNode(),        caseStudyStartYearFocusNode: FocusNode(),        isNewController: TextEditingController(),        sponsorShipController: TextEditingController(),        errorMessageController: TextEditingController(),        highestQualification: false,        // showCurrentlyStudying: !isAlreadyCurrentlyStudying,        showCurrentlyStudying: false,        currentlyStudying: false,        lastTerm: [],        graduationLevel: [],        university: [],        otherUniversityController: TextEditingController(),        otherUniversityFocusNode: FocusNode(),        graduationEndDateController: TextEditingController(),        graduationEndDateFocusNode: FocusNode(),        sponsorShipFocusNode: FocusNode(),      ));    });  }  Widget _graduationDetailsSection( {required int step, required LanguageChangeViewModel langProvider}){    return GraduationInformationView(selectSponsorshipErrorText: _selectSponsorshipErrorText,onUpdateHavingSponsor: updateHavingSponsor,graduationDetailsList: _graduationDetailsList,graduationLevelMenuItems: _graduationLevelMenuItems,graduationLevelDDSMenuItems: _graduationLevelDDSMenuItems,caseStudyYearDropdownMenuItems: _caseStudyYearDropdownMenuItems, academicCareer: _selectedScholarship?.acadmicCareer,scholarshipType: _selectedScholarship?.scholarshipType,nationalityMenuItemsList: _nationalityMenuItemsList, displayHighSchool: displayHighSchool(), draftPrevNextButtons: draftPrevNextButtons(langProvider), havingSponsor: havingSponsor, addGraduation: _addGraduationDetail,);  }  /// *--------------------------------------------------------------- Graduation Details Section end ----------------------------------------------------------------------------*  /// *--------------------------------------------------------------- University And Majors Section Start ----------------------------------------------------------------------------*  /// step-4  /// "acadProgram": "SCO-P",  /// "acadProgramDds": "FELL",  /// "acadProgramPgrd": "MSTRS",  /// controllers, focus nodes and error text variables for Academic program  // final TextEditingController _acadProgramController = TextEditingController();  final TextEditingController _acadProgramDdsController = TextEditingController();  final TextEditingController _acadProgramPgrdController = TextEditingController();  // final FocusNode _acadProgramFocusNode = FocusNode();  final FocusNode _acadProgramDdsFocusNode = FocusNode();  final FocusNode _acadProgramPgrdFocusNode = FocusNode();  String? _acadProgramErrorText;  String? _acadProgramDdsErrorText;  String? _acadProgramPgrdErrorText;  /// list of Academic Program PGRD  List<DropdownMenuItem> _acadProgramPgrdMenuItemsList = [];  /// list of Academic Program DDS  List<DropdownMenuItem> _acadProgramDdsMenuItemsList = [];  List<DropdownMenuItem> getMajors() {    final langProvider = Provider.of<LanguageChangeViewModel>(context, listen: false);    /// Step 1: Check for postgraduate academic career ("PGRD")    if (_selectedScholarship?.acadmicCareer?.toUpperCase() == "PGRD") {      String majorCriteria = "MAJORSPGRD#${_selectedScholarship?.acadmicCareer?.toUpperCase()}#${isStudyCountry ? 'N' : 'Y'}";      return populateCommonDataDropdown(        menuItemsList: Constants.lovCodeMap[majorCriteria]?.values ?? [],        provider: langProvider,      );    }    /// Step 2: Default major criteria for non-PGRD    String majorCriteria = "MAJORS#${_selectedScholarship?.acadmicCareer?.toUpperCase()}#${isStudyCountry ? 'N' : 'Y'}";    List<dynamic> items = Constants.lovCodeMap[majorCriteria]?.values ?? [];    /// Step 3: Check for different admit types    if (_selectedScholarship?.admitType?.toUpperCase() == "ACT") {      /// For "ACT" admit type      majorCriteria = "MAJORSACT#${_selectedScholarship?.acadmicCareer?.toUpperCase()}#${isStudyCountry ? 'N' : 'Y'}";      items = Constants.lovCodeMap[majorCriteria]?.values ?? [];    } else if (_selectedScholarship?.admitType?.toUpperCase() == "NLU") {      /// For "NLU" admit type      majorCriteria = "MAJORSNL#${_selectedScholarship?.acadmicCareer?.toUpperCase()}#${isStudyCountry ? 'N' : 'Y'}";      items = Constants.lovCodeMap[majorCriteria]?.values ?? [];    } else if (_selectedScholarship?.scholarshipType?.toUpperCase() == "INT" && _selectedScholarship?.admitType?.toUpperCase() != "MET") {      /// Handle "INT" admit type with specific filtering for "OTH"      List<dynamic> filteredItems = [];      for (var item in items) {        if (item.code?.toUpperCase() == "OTH") {          if (_isValidAdmitTypeForINT(_selectedScholarship?.admitType,              _selectedScholarship?.configurationKey)) {            filteredItems.add(item);          }        } else {          filteredItems.add(item); /// Add other items directly        }      }      items = filteredItems; /// Update items with filtered items    } else if (_selectedScholarship?.scholarshipType?.toUpperCase() == "EXT") {      /// For "EXT" scholarship type, items already fetched    } else {      /// Default case to only add "BAM" items      List<dynamic> filteredItems = [];      for (var item in items) {        if (item.code?.toUpperCase() == "BAM") {          filteredItems.add(item);        }      }      items = filteredItems; /// Update items with filtered items    }    /// Step 4: Handle special cases    if (_selectedScholarship?.isSpecialCase ?? false) {      items.add({'value': 'OTH', 'label': ''}); /// Append special case "OTH"    }    /// Return the final list of majors    return populateCommonDataDropdown(        menuItemsList: items, provider: langProvider);  }/// Helper method to validate INT admit type  bool _isValidAdmitTypeForINT(String? admitType, String? configurationKey) {    return ["MOP", "MOS"].contains(admitType?.toUpperCase()) ||        configurationKey?.toUpperCase() == "SCOUGRDINTHH";  }/// major dropdown menu items list  List<DropdownMenuItem> _majorsMenuItemsList = [];  bool isStudyCountry = false;  /// majors wishlist/// Initialize the list  final List<MajorWishList> _majorsWishlist = [];/// Function to add a new MajorWishList item  void addMajorWishList() {    /// Create a new MajorWishList instance    MajorWishList newWishList = MajorWishList(      majorController: TextEditingController(),      otherMajorController: TextEditingController(),      errorMessageController: TextEditingController(),      isNewController: TextEditingController(),      majorFocusNode: FocusNode(),      otherMajorFocusNode: FocusNode(),      errorMessageFocusNode: FocusNode(),      isNewFocusNode: FocusNode(),    );    /// Add the new instance to the list    _majorsWishlist.add(newWishList);  }/// Function to remove a MajorWishList item by index  void removeMajorWishList(int index) {    if (index >= 0 && index < _majorsWishlist.length) {      /// Dispose the controllers and focus nodes to avoid memory leaks      _majorsWishlist[index].majorController.dispose();      _majorsWishlist[index].errorMessageController.dispose();      _majorsWishlist[index].isNewController.dispose();      _majorsWishlist[index].majorFocusNode.dispose();      _majorsWishlist[index].errorMessageFocusNode.dispose();      _majorsWishlist[index].isNewFocusNode.dispose();      /// Remove the item from the list      _majorsWishlist.removeAt(index);    }  }  /// university priority list/// List to store UniversityPriority items final List<UniversityPriority> _universityPriorityList = [];  List<DropdownMenuItem> populateUniversitiesWishList(      UniversityPriority universityInfo)  {    String country = universityInfo.countryIdController.text;    /// Step 1: Fetch initial list of universities based on country    List<DropdownMenuItem> items = fetchListOfValue("UNIVERSITY#$country#UNV");    /// List to store final items    List<DropdownMenuItem> itemsNew = [];    /// Step 2: Handle special case    if (_selectedScholarship?.isSpecialCase ?? false) {      itemsNew.add(const DropdownMenuItem(          value: "OTH", child: Text(""))); /// "OTH" means "Other"    }    /// Step 3: Check for different admit types    if (_selectedScholarship?.admitType?.toUpperCase() == "NLU") {      /// For "NLU" admit type      itemsNew = fetchListOfValue("EXTUNIVERSITYNL#$country#UNV");    } else if (country.toUpperCase() == "GBR") {      /// For country "GBR"      itemsNew.add(const DropdownMenuItem(value: "OTH", child: Text("")));    } else if (country.toUpperCase() != "ARE" && _selectedScholarship?.scholarshipType?.toUpperCase() != "INT") {      /// For countries not equal to "ARE" and scholarship type not "INT"      itemsNew = fetchListOfValue("GRAD_UNIVERSITY#$country#UNV");    } else if (_selectedScholarship?.scholarshipType?.toUpperCase() == "INT" &&        _selectedScholarship?.admitType?.toUpperCase() == "MET") {      /// For "INT" scholarship type with "MET" admit type      for (var item in items) {        if (item.value.toString().toUpperCase() == "00000105") {          itemsNew.add(item);        }      }    } else {      /// Default case, add all items      itemsNew.addAll(items);    }    universityInfo.universityDropdown = itemsNew;    return itemsNew;  }/// Helper function to mimic fetchListOfValue, should return a list of DropdownMenuItem  List<DropdownMenuItem> fetchListOfValue(String key) {    /// Your logic to fetch values goes here    final langProvider = Provider.of<LanguageChangeViewModel>(context, listen: false);    if (Constants.lovCodeMap[key]?.values != null) {      return populateCommonDataDropdown(        menuItemsList: Constants.lovCodeMap[key]!.values!,        provider: langProvider,        textColor: AppColors.scoButtonColor,      );    } else {      /// Handle the case where the values are null (e.g., return an empty list or log an error)      return []; /// or any appropriate fallback    }  }  List<DropdownMenuItem> _universityPriorityStatus = [];/// Function to add a new UniversityPriority item with only countryId provided  void addUniversityPriority() {    /// Create a new UniversityPriority instance with only countryId set    UniversityPriority newPriority = UniversityPriority(      /// countryIdController: TextEditingController(text: _selectedScholarship?.acadmicCareer == 'UGRD' ? "ARE" : ''),      countryIdController: TextEditingController(text: isStudyCountry ? "ARE" : ''),      universityIdController: TextEditingController(),      otherUniversityNameController: TextEditingController(),      majorsController: TextEditingController(),      otherMajorsController: TextEditingController(),      statusController: TextEditingController(),      errorMessageController: TextEditingController(),      isNewController: TextEditingController(text: 'true'),      countryIdFocusNode: FocusNode(),      universityIdFocusNode: FocusNode(),      otherUniversityNameFocusNode: FocusNode(),      majorsFocusNode: FocusNode(),      otherMajorsFocusNode: FocusNode(),      statusFocusNode: FocusNode(),    );    /// Add the new instance to the list    _universityPriorityList.add(newPriority);    populateUniversitiesWishList(newPriority);  }/// Function to remove a UniversityPriority item by index  void removeUniversityPriority(int index) {    if (index >= 0 && index < _universityPriorityList.length) {      /// Dispose the controllers and focus nodes to avoid memory leaks      _universityPriorityList[index].countryIdController.dispose();      _universityPriorityList[index].universityIdController.dispose();      _universityPriorityList[index].otherUniversityNameController.dispose();      _universityPriorityList[index].majorsController.dispose();      _universityPriorityList[index].statusController.dispose();      _universityPriorityList[index].errorMessageController.dispose();      _universityPriorityList[index].isNewController.dispose();      _universityPriorityList[index].countryIdFocusNode.dispose();      _universityPriorityList[index].universityIdFocusNode.dispose();      _universityPriorityList[index].otherUniversityNameFocusNode.dispose();      _universityPriorityList[index].majorsFocusNode.dispose();      _universityPriorityList[index].statusFocusNode.dispose();      /// Remove the item from the list      _universityPriorityList.removeAt(index);    }  }  _universityAndMajorsDetailsSection(      {required int step, required LanguageChangeViewModel langProvider}) {    final localization = AppLocalizations.of(context)!;    return Container(        padding: EdgeInsets.symmetric(horizontal: kPadding),        color: Colors.grey.shade200,        child: SingleChildScrollView(            child: Column(children: [          // draftPrevNextButtons(langProvider),              kSmallSpace,          /// majors          CustomInformationContainer(              title: _selectedScholarship?.acadmicCareer == 'PGRD'                  ? localization.pgrdMajorWishlist                  : localization.majorWishlist,               expandedContent: sectionBackground(child: Column(                   crossAxisAlignment: CrossAxisAlignment.start,                   mainAxisSize: MainAxisSize.min,                   children: [                     /// dropdown for pgrd students academic program                     if(_selectedScholarship?.acadmicCareer == 'PGRD' && _selectedScholarship?.acadmicCareer != 'DDS')                       Column(                         children: [                           fieldHeading(                             title: localization.pgrdAdacProgram,                             important: true,                             langProvider: langProvider,                           ),                           scholarshipFormDropdown(context:context,                             controller: _acadProgramPgrdController,                             currentFocusNode: _acadProgramPgrdFocusNode,                             menuItemsList: _acadProgramPgrdMenuItemsList,                             hintText: localization.select,                             errorText: _acadProgramPgrdErrorText,                             onChanged: (value) {                               _acadProgramPgrdErrorText = null;                              