=            populateCommonDataDropdown(                menuItemsList: Constants                    .lovCodeMap[                        'EXAMINATION_TYPE#${_requiredExaminationList[index].examinationController.text}']!                    .values!,                provider: langProvider,                textColor: AppColors.scoButtonColor);      }    });  }  /// get min score and max score just fetching the elements from the lov and based on exam and exam type selection we will find min and max score and set that to the fields  List _testScoreVal = [];/// function to add examination from list  _addRequiredExamination() {    setState(() {      _requiredExaminationList.add(RequiredExaminations(        examinationController: TextEditingController(),        examinationTypeIdController: TextEditingController(),        examinationGradeController: TextEditingController(),        minScoreController: TextEditingController(),        maxScoreController: TextEditingController(),        examDateController: TextEditingController(),        isNewController: TextEditingController(text: "true"),        errorMessageController: TextEditingController(),        examinationFocusNode: FocusNode(),        examinationTypeIdFocusNode: FocusNode(),        examinationGradeFocusNode: FocusNode(),        minScoreFocusNode: FocusNode(),        maxScoreFocusNode: FocusNode(),        examDateFocusNode: FocusNode(),        examinationTypeDropdown: [],      ));    });  }  /// Section for Required Examinations  Widget _requiredExaminationsDetailsSection(      {required int step, required LanguageChangeViewModel langProvider}) {    return RequiredExaminationsView(        draftPrevNextButtons: draftPrevNextButtons(langProvider),        acadmicCareer: _selectedScholarship?.acadmicCareer,        requiredExaminationList: _requiredExaminationList,        requiredExaminationDropdownMenuItems: _requiredExaminationDropdownMenuItems,        testScoreVal: _testScoreVal,    );  }  /// *--------------------------------------------------------------- Required Examinations end ----------------------------------------------------------------------------*  /// *--------------------------------------------------------------- Employment history section start ----------------------------------------------------------------------------*  /// step-6/// function to check weather to display employment history or not  bool displayEmploymentHistory() {    final key = _selectedScholarship?.configurationKey;    return shouldShowEmploymentHistory(key);  }  /// available employment status from lov  List _employmentStatusItemsList = [];  /// current Employment status  String? _employmentStatus;  String? _selectEmploymentHistoryErrorText;  /// employment history list final List<EmploymentHistory> _employmentHistoryList = [];  /// add employment history  void _addEmploymentHistory() {    setState(() {      _employmentHistoryList.add(EmploymentHistory(        employerNameController: TextEditingController(),        designationController: TextEditingController(),        startDateController: TextEditingController(),        endDateController: TextEditingController(),        occupationController: TextEditingController(),        titleController: TextEditingController(),        placeController: TextEditingController(),        reportingManagerController: TextEditingController(),        contactNumberController: TextEditingController(),        contactEmailController: TextEditingController(),        isNewController: TextEditingController(),        errorMessageController: TextEditingController(),        employerNameFocusNode: FocusNode(),        designationFocusNode: FocusNode(),        startDateFocusNode: FocusNode(),        endDateFocusNode: FocusNode(),        occupationFocusNode: FocusNode(),        titleFocusNode: FocusNode(),        placeFocusNode: FocusNode(),        reportingManagerFocusNode: FocusNode(),        contactNumberFocusNode: FocusNode(),        contactEmailFocusNode: FocusNode(),      ));    });  }  Widget _employmentHistoryDetailsSection(      {required int step, required LanguageChangeViewModel langProvider}) {    return EmploymentHistoryView(      selectEmploymentHistoryErrorText: _selectEmploymentHistoryErrorText,      onEmploymentStatusChanged: (value) {      setState(() {        _employmentStatus = value;        if (_employmentStatus == 'N') {          _employmentHistoryList.clear();        }        // if (_employmentHistoryList.isEmpty) {        //   _addEmploymentHistory();        // }        else {          _employmentHistoryList.clear();          _addEmploymentHistory();        }        _selectEmploymentHistoryErrorText = null;      });    },      employmentHistoryList: _employmentHistoryList,      employmentStatus: _employmentStatus,      employmentStatusItemsList: _employmentStatusItemsList,      draftPrevNextButtons: draftPrevNextButtons(langProvider),    );  }  /// *--------------------------------------------------------- Employment History Section Section end ------------------------------------------------------------------------------*  /// *--------------------------------------------------------- Attachments Section start ------------------------------------------------------------------------------*  /// step-7  /// List of Attachments:  List _attachmentsList = [];  final List<Attachment> _myAttachmentsList = [];  /// Determine required conditions for each nationality type  bool shouldShowAttachment(Attachment attachment) {    if (_passportNationalityController.text == 'ARE' && (_isMotherUAECheckbox == false) ) {      return attachment.requiredController.text == 'MRL' || attachment.requiredController.text == 'OPL';    }    else if (_passportNationalityController.text != 'ARE' && (_isMotherUAECheckbox == false)) {      return attachment.requiredController.text == 'NMRL' || attachment.requiredController.text == 'NOPL';    }    else if (_passportNationalityController.text != 'ARE' && _isMotherUAECheckbox) {      return attachment.requiredController.text == 'XMRL' || attachment.requiredController.text == 'XOPL';    }    return false;  }  /// Create clean lists for valid attachments  final List<Values> _filteredAttachmentsList = []; /// These are lov values  final List<Attachment> _filteredMyAttachmentsList = [];  void filterAttachments() {    _filteredAttachmentsList.clear();    _filteredMyAttachmentsList.clear();    for (int i = 0; i < _myAttachmentsList.length; i++) {      final attachment = _attachmentsList[i];      final myAttachment = _myAttachmentsList[i];      // Check the condition and filter accordingly      // print("isStudyCountry: ${isStudyCountry}");      if ( isStudyCountry ) {        if(shouldShowAttachment(myAttachment)){          _filteredAttachmentsList.add(attachment); // Add to filtered list          _filteredMyAttachmentsList.add(myAttachment); //Add corresponding myAttachment        }      }      /// Skip all the rules if the scholarship type is EXt      if(!isStudyCountry){        _filteredAttachmentsList.add(attachment); // Add to filtered list        _filteredMyAttachmentsList.add(myAttachment); // Add corresponding myAttachment      }    }  }  Widget _attachmentsSection(      {required int step, required LanguageChangeViewModel langProvider}) {  final localization = AppLocalizations.of(context)!;    // Filter the lists before building the UI    filterAttachments();    // print(_filteredAttachmentsList.length);    return Container(        padding: EdgeInsets.symmetric(horizontal: kPadding),        color: Colors.grey.shade200,        child: SingleChildScrollView(            child: Column(children: [              // draftPrevNextButtons(langProvider),              kSmallSpace,              CustomInformationContainer(                  title: localization.attachments,                  expandedContent: Column(                      crossAxisAlignment: CrossAxisAlignment.start,                      mainAxisSize: MainAxisSize.min,                      children: [                        // Use the filtered lists for the ListView                        sectionBackground(child: ListView.builder(                          shrinkWrap: true,                          physics: const NeverScrollableScrollPhysics(),                          itemCount: _filteredMyAttachmentsList.length, // Use filtered length                          itemBuilder: (context, index) {                            // final attachment = _filteredAttachmentsList[index]; // From filtered list                            final myAttachment = _filteredMyAttachmentsList[index]; // From filtered list                            File? file;                            return Consumer<AttachFileViewmodel>(                              builder: (context, provider, _) {                                return AttachFile(                                  attachmentNumber: index+1,                                  selectedCheckListCode: _selectedCheckListCode ?? '',                                  // attachment: attachment, // Use the filtered attachment                                  myAttachment: myAttachment, // Use the filtered myAttachment                                  onAction: () {                                    // Reset file and clear fields                                    setState(() {                                      myAttachment.userFileNameController.text = "";                                      myAttachment.base64StringController.text = "";                                      myAttachment.commentController.text = '';                                      myAttachment.descriptionController.text = '';                                      file = null;                                    });                                  },                                  onPressed: () async {                                    // Check permission before picking file                                    // final permitted = await _permissionServices.checkAndRequestPermission(Platform.isIOS ? Permission.storage :  Permission.manageExternalStorage, context);                                    if (true) {                                      // Define allowed extensions based on document code                                      final allowedExtensions = myAttachment.documentCdController.text.toUpperCase() == 'SEL006' ? ['jpg', 'jpeg'] : ['pdf'];                                      // set allowed extensions                                      myAttachment.supportedFileTypeController.text = myAttachment.documentCdController.text.toUpperCase() == 'SEL006' ? ".jpeg|.jpg|.JPEG|.JPG" : ".pdf|.PDF";                                      /// set File Type (fir photo 1 and for pdf its 2)                                      myAttachment.supportedFileTypeController.text = myAttachment.supportedFileTypeController.text.toUpperCase() == ".jpeg|.jpg|.JPEG|.JPG" ? "1" : "2";                                      file = await _mediaServices.getSingleFileFromPicker(allowedExtensions: allowedExtensions);                                      if (file != null) {                                        setProcessingMode(status: true);                                        // Update filename and base64 content before asynchronous work                                        myAttachment.userFileNameController.text = file!.path.split('/').last;                                        myAttachment.base64StringController.text =  base64Encode(file!.readAsBytesSync());                                        myAttachment.applictantIdController.text = draftId.toString();                                        myAttachment.applicationDetailIdController.text = draftId.toString();                                        myAttachment.emiratesIdController.text = _emiratesIdController.text;                                        // Perform asynchronous work outside of setState                                        final result = await provider.attachFile(file: myAttachment.toJson());                                        if (result) {                                          // On success, don't send base64String in draft, just send it empty                                          myAttachment.fileUploadedController.text = 'true';                                          myAttachment.base64StringController.clear();                                        }                                        // Synchronously update the UI state after async work completes                                        setState(() {                                          setProcessingMode(status: false);                                          if (!result) {                                            // Reset file and clear fields if attachFile fails                                            myAttachment.userFileNameController.text = "";                                            myAttachment.base64StringController.text = "";                                            myAttachment.commentController.text = '';                                            myAttachment.fileUploadedController.text = 'false';                                            file = null;                                          }                                        });                                      }                                    }                                  },                                );                              },                            );                          },                        ),)                      ])),              const SizedBox(height: 100,),              // draftPrevNextButtons(langProvider)            ])));  }  /// *--------------------------------------------------------- Attachments Section end ------------------------------------------------------------------------------*  bool _iAgreeTerms = false;  String? _iAgreeErrorText;   bool _applicationSubmitted = false;  dynamic _applicationNumber;  Widget _confirmation()  {    final localization = AppLocalizations.of(context)!;    final langProvider = Provider.of<LanguageChangeViewModel>(context,listen: false);    return Container(      decoration: BoxDecoration(        color: AppColors.lightGrey,        borderRadius: BorderRadius.circular(5),      ),      child: Padding(        padding:  EdgeInsets.all(kPadding),        child: Column(          mainAxisAlignment: MainAxisAlignment.spaceBetween,          children: [         // if(!_applicationSubmitted)  draftPrevNextButtons(langProvider),            CustomInformationContainer(title: localization.submissionConfirmation ,          expandedContent: Column(          crossAxisAlignment: CrossAxisAlignment.start,          children: [            kFormHeight,            CustomGFCheckbox(value: _iAgreeTerms, onChanged: (value){              setState(() {                _iAgreeTerms = value ?? false;              });            }, text: localization.submissionAgree) ,            showErrorText(_iAgreeErrorText),            kFormHeight,            // Padding(            //   padding: const EdgeInsets.symmetric(horizontal: 50),            //   child: CustomButton(buttonName: localization.print, isLoading: false, textDirection: getTextDirection(langProvider), onTap: (){            //     _alertService.toastMessage(localization.comingSoon);            //   },            //     buttonColor: AppColors.scoThemeColor,            //     borderColor: Colors.transparent),            // ),            // kFormHeight,            kFormHeight,          ],        ),          ),            showVoid,            // if(!_applicationSubmitted)  draftPrevNextButtons(langProvider),          ]),      ));  }  /// *--------------------------------------------------------- validate section in accordance with the steps start ------------------------------------------------------------------------------*  /// *--------------------------------------------------------- validate section in accordance with the steps start ------------------------------------------------------------------------------*  /// if section is already fulfilling the requirements then move forward to next step:  Future<bool> validateSection(      {required int step, required LanguageChangeViewModel langProvider}) async  {    String academicCareer = _selectedScholarship?.acadmicCareer ?? '';    String key = _selectedScholarship?.configurationKey ?? '';    /// Helper functions for specific validations    // bool shouldShowHighSchoolDetails() {    //   return acadmicCareer == 'UG' ||    //       acadmicCareer == 'UGRD' ||    //       acadmicCareer == 'SCHL' ||    //       acadmicCareer == 'HCHL';    // }    // bool isUniversityAndMajorsRequired() {    //   return academicCareer != 'SCHL';    // }    // bool isRequiredExaminationDetailsRequired() {    //   return !(academicCareer == 'SCHL' || academicCareer == 'HCHL');    // }    // bool isAttachmentSectionForExt() {    //   return key == 'SCOUPPEXT';    // }    // bool shouldDisplayEmploymentHistory() {    //   return displayEmploymentHistory();    // }    /// Switch case for validation based on step    switch (step) {      case 0:        // print("Inside case 1");        return await validateStudentDetailsSection(langProvider);      case 1:        // print("Inside case 2");        if (shouldShowHighSchoolDetails(academicCareer)) {          if (!await validateHighSchoolDetails(langProvider)) return false;        }        return await validateGraduationDetails(langProvider);      case 2:        // print("Inside case 3");        if (shouldShowUniversityAndMajors(academicCareer)) {          return validateUniversityAndMajorsDetails(langProvider);        }        return true;      case 3:        // print("Inside case 4");        if (shouldShowAttachmentSectionForExt(configurationKey: key)) {          return await validateAttachmentsSection(langProvider); /// Validation for SCOUPPEXT at step 4        } else if (shouldShowRequiredExaminations(academicCareer)) {          return await validateRequiredExaminations(langProvider);        }        return true;      case 4:        // print("Inside case 5");        if (academicCareer == 'UGRD') {          return await validateAttachmentsSection(langProvider); /// Validation for UGRD at step 5        } else if(shouldShowAttachmentSectionForExt(configurationKey: key)){          return validateStudentConfirmationSection(langProvider);        }        else if (shouldShowEmploymentHistory(key)) {          return await validateEmploymentHistory(langProvider);        }        return true;      case 5:        // print("Inside case 6");        if (academicCareer == 'UGRD') {          return validateStudentConfirmationSection(langProvider);        }        else if(shouldShowAttachmentSectionForExt(configurationKey: key)){          return validateStudentUndertakingSection(langProvider);        }          return await  validateAttachmentsSection(langProvider); /// Final confirmation for attachments if applicable      case 6:        if(academicCareer == 'UGRD'){          return validateStudentUndertakingSection(langProvider);        } else{          return validateStudentConfirmationSection(langProvider);        }        return true; /// No validation required for confirmation      case 7:      // print("Inside case 0");        return validateStudentUndertakingSection(langProvider);      default:        return true;    }  }  /// To request focus where field needs to adjust:  FocusNode? firstErrorFocusNode;  /// Step 0: Validate "Accept Student Undertaking" section  bool validateStudentUndertakingSection(langProvider) {    firstErrorFocusNode = null;    _acceptStudentUndertakingErrorText = null;    /// Add validation logic for student details    if (!_acceptStudentUndertaking) {      // _alertService.flushBarErrorMessages(      //     message:  AppLocalizations.of(context)!.acceptTermsAndConditions,      //     // context: context,      //     provider: langProvider);      _acceptStudentUndertakingErrorText = AppLocalizations.of(context)!.studentUndertakingRequired;      setState(() {});      // _alertService.showErrorSnackBar(AppLocalizations.of(context)!.studentUndertakingRequired);      return false;    }    // _alertService.showToast(message: "message");    // ScaffoldMessenger.of(context).showSnackBar(SnackBar(content: Text("Accept")));    return true;  }  bool validateStudentConfirmationSection(langProvider) {    firstErrorFocusNode = null;    _iAgreeErrorText = null;    /// Add validation logic for student details    if (!_iAgreeTerms) {      // _alertService.flushBarErrorMessages(      //     message:  AppLocalizations.of(context)!.acceptTermsAndConditions,      //     // context: context,      //     provider: langProvider);      // _alertService.showErrorSnackBar(AppLocalizations.of(context)!.submissionAgreeRequired);      _iAgreeErrorText = AppLocalizations.of(context)!.submissionAgreeRequired;      return false;    }    // _alertService.showToast(message: "message");    // ScaffoldMessenger.of(context).showSnackBar(SnackBar(content: Text("Accept")));    return true;  }  Future<bool> validateStudentDetailsSection(langProvider)async {final localization = AppLocalizations.of(context)!;    firstErrorFocusNode = null;    /// Validate Arabic Name fields    if (_arabicName.studentNameController.text.isEmpty || !Validations.isArabicNameValid(_arabicName.studentNameController.text)) {      setState(() {        _arabicName.studentNameError = localization.studentNameArabicValidate;        firstErrorFocusNode ??= _arabicName.studentNameFocusNode; /// Set firstErrorFocusNode if it's null      });    }    if (_arabicName.fatherNameController.text.isEmpty ||        !Validations.isArabicNameValid(_arabicName.fatherNameController.text)) {      setState(() {        _arabicName.fatherNameError = localization.fatherNameArabicValidate;        firstErrorFocusNode ??=            _arabicName.fatherNameFocusNode; /// Set firstErrorFocusNode if it's null      });    }    if (_arabicName.grandFatherNameController.text.isEmpty ||        !Validations.isArabicNameValid(_arabicName.grandFatherNameController.text)) {      setState(() {        _arabicName.grandFatherNameError = localization.grandfatherNameArabicValidate;        firstErrorFocusNode ??= _arabicName.grandFatherNameFocusNode;      });    }    if (_arabicName.familyNameController.text.isEmpty ||        !Validations.isArabicNameValid(_arabicName.familyNameController.text)) {      setState(() {        _arabicName.familyNameError = localization.familyNameArabicValidate;        firstErrorFocusNode ??= _arabicName.familyNameFocusNode;      });    }    /// Validate English Name fields    if (_englishName.studentNameController.text.isEmpty ||        !Validations.isEnglishNameValid(_englishName.studentNameController.text)) {      setState(() {        _englishName.studentNameError = localization.studentNameEnglishValidate;        firstErrorFocusNode ??= _englishName.studentNameFocusNode;      });    }    if (_englishName.fatherNameController.text.isEmpty ||        !Validations.isEnglishNameValid(_englishName.fatherNameController.text)) {      setState(() {        _englishName.fatherNameError = localization.fatherNameEnglishValidate;        firstErrorFocusNode ??= _englishName.fatherNameFocusNode;      });    }    if (_englishName.grandFatherNameController.text.isEmpty ||        !Validations.isEnglishNameValid(            _englishName.grandFatherNameController.text)) {      setState(() {        _englishName.grandFatherNameError = localization.grandfatherNameEnglishValidate;        firstErrorFocusNode ??= _englishName.grandFatherNameFocusNode;      });    }    if (_englishName.familyNameController.text.isEmpty ||        !Validations.isEnglishNameValid(_englishName.familyNameController.text)) {      setState(() {        _englishName.familyNameError = localization.familyNameEnglishValidate;        firstErrorFocusNode ??= _englishName.familyNameFocusNode;      });    }    /// passport data validation start    if (_passportNationalityController.text.isEmpty) {      setState(() {        _passportNationalityError = localization.nationalityRequired;        firstErrorFocusNode ??= _passportNationalityFocusNode;      });    }    if (_passportNumberController.text.isEmpty) {      setState(() {        _passportNumberError = localization.passportNumberValidate;        firstErrorFocusNode ??= _passportNumberFocusNode;      });    }    if (_passportIssueDateController.text.isEmpty) {      setState(() {        _passportIssueDateError = localization.passportIssueDateValidate;        firstErrorFocusNode ??= _passportIssueDateFocusNode;      });    }    if (_passportExpiryDateController.text.isEmpty) {      setState(() {        _passportExpiryDateError = localization.passportExpireDateValidate;        firstErrorFocusNode ??= _passportExpiryDateFocusNode;      });    }    if(_passportIssueDateController.text == _passportExpiryDateController.text ){      setState(() {        _passportExpiryDateError = "${localization.passportExpireDateValidate}\nStart and End Date cannot be same";        firstErrorFocusNode ??= _passportExpiryDateFocusNode;      });    }    if (_passportPlaceOfIssueController.text.isEmpty) {      setState(() {        _passportPlaceOfIssueError = localization.passportIssueDateValidate;        firstErrorFocusNode ??= _passportPlaceOfIssueFocusNode;      });    }    // if( _selectedScholarship?.admitType != 'INT' ){    //   if (_passportUnifiedNoController.text.isNotEmpty && !Validations.isUnifiedNumberValid(_passportUnifiedNoController.text)) {    //     setState(() {    //       _passportUnifiedNoError = AppLocalizations.of(context)!.unifiedNumberRequired;    //       firstErrorFocusNode ??= _passportUnifiedNoFocusNode;    //     });    //   }    // }    /// personal details validation    /// student profile provider for fetching pre-filling student details and verify that emirate id and email matches the profile    final studentProfileProvider = Provider.of<GetPersonalDetailsViewModel>(context, listen: false);    if (_emiratesIdController.text.isEmpty|| _emiratesIdController.text.replaceAll('-', '') != studentProfileProvider.apiResponse.data?.data?.user?.emirateId?.replaceAll('-', '') ) {      setState(() {        _emiratesIdError = localization.emiratesIdValidate;        firstErrorFocusNode ??= _emiratesIdFocusNode;      });    }    if (_emiratesIdExpiryDateController.text.isEmpty) {      setState(() {        _emiratesIdExpiryDateError = localization.emirateidExpiryDateRequired;        firstErrorFocusNode ??= _emiratesIdExpiryDateFocusNode;      });    }    if (_dateOfBirthController.text.isEmpty) {      setState(() {        _dateOfBirthError = localization.brithDateValidate;        firstErrorFocusNode ??= _dateOfBirthFocusNode;      });    }    if(!isFourteenYearsOld(_dateOfBirthController.text)){      setState(() {        _dateOfBirthError = localization.brithDateValidate;        firstErrorFocusNode ??= _dateOfBirthFocusNode;      });    }    if (_placeOfBirthController.text.isEmpty) {      setState(() {        _placeOfBirthError = localization.birthPlaceValidate;        firstErrorFocusNode ??= _placeOfBirthFocusNode;      });    }    if (_genderController.text.isEmpty) {      setState(() {        _genderError = localization.genderValidate;        firstErrorFocusNode ??= _genderFocusNode;      });    }    if (_maritalStatusController.text.isEmpty) {      setState(() {        _maritalStatusError = localization.maritalStatusValidate;        firstErrorFocusNode ??= _maritalStatusFocusNode;      });    }    if (_studentEmailController.text.isEmpty || _studentEmailController.text != studentProfileProvider.apiResponse.data?.data?.user?.emailAddress  ) {      setState(() {        // _studentEmailError = 'Please enter your student email.\nYour email address must match with profile email address.';        _studentEmailError = localization.emailNotMatch;        firstErrorFocusNode ??= _studentEmailFocusNode;      });    }    ///validate the family information    if (_passportNationalityController.text == "ARE") {      if (_familyInformationEmiratesController.text.isEmpty) {        setState(() {          _familyInformationEmiratesErrorText = localization.emiratesValidate;          firstErrorFocusNode ??= _familyInformationEmiratesFocusNode;        });      }      if (_familyInformationTownVillageNoController.text.isEmpty) {        setState(() {          _familyInformationTownVillageNoErrorText = localization.numberOfTownValidate;          firstErrorFocusNode ??= _familyInformationTownVillageNoFocusNode;        });      }      if (_familyInformationParentGuardianNameController.text.isEmpty) {        setState(() {          _familyInformationParentGuardianNameErrorText = localization.parentNameValidate;          firstErrorFocusNode ??= _familyInformationParentGuardianNameFocusNode;        });      }      if (_familyInformationRelationTypeController.text.isEmpty) {        setState(() {          _familyInformationRelationTypeErrorText = localization.relationTypeValidate;          firstErrorFocusNode ??= _familyInformationRelationTypeFocusNode;        });      }      if (_familyInformationMotherNameController.text.isEmpty) {        setState(() {          _familyInformationMotherNameErrorText = localization.motherNameValidate;          firstErrorFocusNode ??= _familyInformationMotherNameFocusNode;        });      }    }    /// validate the Relative information  _isRelativeStudyingFromScholarshipErrorText = null;  if (_isRelativeStudyingFromScholarship == null) {      setState(() {        firstErrorFocusNode ??= _isRelativeStudyingFromScholarshipYesFocusNode;        _isRelativeStudyingFromScholarshipErrorText = AppLocalizations.of(context)!.relativesStudyingScholarshipRequired;        // _alertService.showToast(message: AppLocalizations.of(context)!.relativesStudyingScholarshipRequired);      });    }    if (_relativeInfoList.isNotEmpty && _isRelativeStudyingFromScholarship != null) {      for (var element in _relativeInfoList) {        if (element.relativeNameController.text.isEmpty) {          setState(() {            element.relativeNameError = localization.relativeNameValidate;            firstErrorFocusNode ??= element.relationTypeFocusNode;          });        }        if (element.relationTypeController.text.isEmpty) {          setState(() {            element.relationTypeError = localization.relationTypeValidate;            firstErrorFocusNode ??= element.relationTypeFocusNode;          });        }        if (element.countryUniversityController.text.isEmpty) {          setState(() {            element.countryUniversityError = localization.universityValidate;            firstErrorFocusNode ??= element.relationTypeFocusNode;          });        }        /// if (element.familyBookNumberController.text.isEmpty) {        ///   setState(() {        ///     element.familyBookNumberError =        ///         "Please Enter your family book number";        ///     firstErrorFocusNode ??= element.familyBookNumberFocusNode;        ///   });        /// }      }    }    /// validate the Phone Number information    if (_phoneNumberList.isNotEmpty) {      for (var element in _phoneNumberList) {        if (element.phoneTypeController.text.isEmpty || element.phoneTypeError != null) {          setState(() {            element.phoneTypeError = localization.submissionPhoneTypeRequired;            firstErrorFocusNode ??= element.phoneTypeFocusNode;          });        }        if (element.phoneNumberController.text.isEmpty || !Validations.isPhoneNumberValid(element.phoneNumberController.text) || element.phoneNumberError != null) {          setState(() {            element.phoneNumberError = localization.phoneNumberOnly;            firstErrorFocusNode ??= element.phoneNumberFocusNode;          });        }        if (element.countryCodeController.text.isEmpty ) {          setState(() {            element.countryCodeError = localization.submissionCountryCodeRequired;            firstErrorFocusNode ??= element.countryCodeFocusNode;          });        }      }    }    /// validate the Address information    if (_addressInformationList.isNotEmpty) {      for (var element in _addressInformationList) {        if (element.addressTypeController.text.isEmpty || element.addressTypeError != null) {          setState(() {            element.addressTypeError = localization.addressTypeValidate;            firstErrorFocusNode ??= element.addressTypeFocusNode;          });        }        if (element.addressLine1Controller.text.isEmpty) {          setState(() {            element.addressLine1Error = localization.addressLine1Validate;            firstErrorFocusNode ??= element.addressLine1FocusNode;          });        }        if (element.countryController.text.isEmpty) {          setState(() {            element.countryError = localization.countryValidate;            firstErrorFocusNode ??= element.countryFocusNode;          });        }        /// if ( element.stateController.text.isEmpty && element.stateDropdownMenuItems?.isNotEmpty ?? false) {        if ( element.stateController.text.isEmpty && (element.stateDropdownMenuItems?.isNotEmpty ?? false)) {        setState(() {            element.stateError = localization.emiratesValidate;            firstErrorFocusNode ??= element.stateFocusNode;          });        }        if (element.cityController.text.isEmpty) {          setState(() {            element.cityError = localization.cityValidate;            firstErrorFocusNode ??= element.cityFocusNode;          });        }      }    }    /// validate military services:    if (_passportNationalityController.text == 'ARE') {      _selectMilitaryServiceStatusErrorText = null;      switch (_isMilitaryService) {        case MilitaryStatus.yes:          if (_militaryServiceStartDateController.text.isEmpty) {            setState(() {              _militaryServiceStartDateErrorText = localization.militaryServiceStartDateValidate;              firstErrorFocusNode ??= _militaryServiceStartDateFocusNode;            });          }          if (_militaryServiceEndDateController.text.isEmpty) {            setState(() {              _militaryServiceEndDateErrorText = localization.militaryServiceEndDateValidate;              firstErrorFocusNode ??= _militaryServiceEndDateFocusNode;            });          }          if (_militaryServiceStartDateController.text == _militaryServiceEndDateController.text) {            setState(() {              _militaryServiceEndDateErrorText = "${localization.militaryServiceEndDateValidate}\nStart Date and end Date cannot be same";              firstErrorFocusNode ??= _militaryServiceEndDateFocusNode;            });          }          break;        case MilitaryStatus.no:          break;        case (MilitaryStatus.exemption || MilitaryStatus.postponed):          if (_reasonForMilitaryController.text.isEmpty) {            setState(() {              _reasonForMilitaryErrorText = localization.militaryReasonValidate;              firstErrorFocusNode ??= _reasonForMilitaryFocusNode;            });          }          break;        case null:          setState(()          {            firstErrorFocusNode = FocusNode();            _selectMilitaryServiceStatusErrorText = "${localization.select} ${localization.militaryServicePanel}";            // _alertService.showErrorSnackBar("${localization.select} ${localization.militaryServicePanel}");          }          );          break;      }    }    /// If any error found, move to the first error focus node    if (firstErrorFocusNode != null) {      FocusScope.of(context).requestFocus(firstErrorFocusNode);      return false;    } else {      _finalForm(isUrlEncoded: true);      /// call save draft on successfull validation      await saveDraft();      return true;    }  }  Future<bool> validateHighSchoolDetails(langProvider)async {    final localization = AppLocalizations.of(context)!;    firstErrorFocusNode = null;    if (displayHighSchool() && _highSchoolList.isNotEmpty) {      for (var element in _highSchoolList) {        if (element.hsLevelController.text.isEmpty || element.hsLevelError != null) {          setState(() {            element.hsLevelError = localization.hsTypeValidate;            firstErrorFocusNode ??= element.hsLevelFocusNode;          });        }        if (element.hsCountryController.text.isEmpty || element.hsCountryError != null) {          setState(() {            element.hsCountryError = localization.countryValidate;            firstErrorFocusNode ??= element.hsCountryFocusNode;          });        }        if (element.schoolStateDropdownMenuItems?.isNotEmpty ?? false) {          if (element.hsStateController.text.isEmpty) {            setState(() {              element.hsStateError = localization.emiratesValidate;              firstErrorFocusNode ??= element.hsStateFocusNode;            });          }        }        if (element.hsCountryController.text == 'ARE') {          if (element.hsNameController.text.isEmpty) {            setState(() {              element.hsNameError = localization.hsNameValidate;              firstErrorFocusNode ??= element.hsNameFocusNode;            });          }        }        if (element.hsCountryController.text != 'ARE' ||            element.hsNameController.text == 'OTH') {            if (element.otherHsNameController.text.isEmpty  || element.otherHsNameError != null) {              setState(() {                element.otherHsNameError = localization.hsnameOtherValidate;                firstErrorFocusNode ??= element.otherHsNameFocusNode;              });            }        }        /// high school type        if (element.hsTypeController.text.isEmpty || element.hsTypeError != null) {          setState(() {            element.hsTypeError = localization.hsTypeValidate;            firstErrorFocusNode ??= element.hsTypeFocusNode;          });        }        /// high school curriculum type        if (element.curriculumTypeController.text.isEmpty || element.curriculumTypeError != null) {          setState(() {            element.curriculumTypeError = localization.curriculumTypesValidate;            firstErrorFocusNode ??= element.curriculumTypeFocusNode;          });        }        /// high school curriculum average        if (element.curriculumAverageController.text.isEmpty || element.curriculumAverageError != null) {          setState(() {            element.curriculumAverageError = localization.curriculumAverageValidate;            firstErrorFocusNode ??= element.curriculumAverageFocusNode;          });        }        if (element.curriculumAverageController.text.isNotEmpty) {          if (element.yearOfPassingController.text.isEmpty || element.yearOfPassingError != null) {            setState(() {              element.yearOfPassingError = localization.hsYearOfPassingValidate;              firstErrorFocusNode ??= element.yearOfPassingFocusNode;            });          }        }        /// Validate high school subject grades and details        if(element.hsDetails.isNotEmpty){          for(var subject in element.hsDetails){            if(subject.required && subject.gradeController.text.isEmpty){              setState(() {                subject.gradeError = localization.gradeValidate;                firstErrorFocusNode ??= subject.gradeFocusNode;              });            }          }          /// validate other high school subjects          if(element.otherHSDetails.isNotEmpty){            for(var subject in element.otherHSDetails){              if(subject.required && subject.otherSubjectNameController.text.isEmpty){                setState(() {                  subject.otherSubjectNameError = localization.otherSubjectNameValidate;                  firstErrorFocusNode ??= subject.otherSubjectNameFocusNode;                });              }              if(subject.required && subject.gradeController.text.isEmpty){                setState(() {                  subject.gradeError = localization.gradeValidate;                  firstErrorFocusNode ??= subject.gradeFocusNode;                });              }            }}}      }    }    if (firstErrorFocusNode != null) {      FocusScope.of(context).requestFocus(firstErrorFocusNode);      return false;    } else {      /// No errors found, return true      /// saving draft      await saveDraft();      return true;    }  }  Future<bool> validateGraduationDetails(langProvider) async{    final localization = AppLocalizations.of(context)!;    firstErrorFocusNode = null;    /// validate graduation details    if (_selectedScholarship?.acadmicCareer != 'SCHL' &&        _selectedScholarship?.acadmicCareer != 'HCHL') {      for (int index = 0; index < _graduationDetailsList.length; index++) {        var element = _graduationDetailsList[index];        validateGraduationDetails(element) {          if (element.currentlyStudying && element.showCurrentlyStudying) {            /// validating last term            if (element.lastTermController.text.isEmpty || element.lastTermError != null) {              setState(() {                element.lastTermError = localization.lastTermRequired;                firstErrorFocusNode ??= element.lastTermFocusNode;              });            }          }/// #################################################################          /// Condition using index and scholarship details          if (index > 0 &&              _selectedScholarship?.acadmicCareer != 'UGRD' &&              _selectedScholarship?.acadmicCareer != 'DDS') {            /// validating graduation level            if (element.levelController.text.isEmpty || element.levelError != null) {              setState(() {                element.levelError = localization.hsGraduationLevelValidate;                firstErrorFocusNode ??= element.levelFocusNode;              });            }          }          /// #################################################################          /// validating dds graduation level          if (index != 0 && _selectedScholarship?.acadmicCareer == 'DDS') {            if (element.levelController.text.isEmpty || element.levelError != null) {              setState(() {                element.levelError = localization.hsGraduationLevelValidate;                firstErrorFocusNode ??= element.levelFocusNode;              });            }          }          /// #################################################################          /// validating graduation country          if (element.countryController.text.isEmpty || element.countryError != null) {            setState(() {              element.countryError = localization.countryValidate;              firstErrorFocusNode ??= element.countryFocusNode;            });          }          /// #################################################################          /// high school university          if (_selectedScholarship?.acadmicCareer != 'DDS') {            if (element.universityController.text.isEmpty || element.universityError !=null) {              setState(() {                element.universityError = localization.hsUniversityValidate;                firstErrorFocusNode ??= element.universityFocusNode;              });            }          }          /// #################################################################          /// other university          if (element.universityController.text == 'OTH') {            if (element.otherUniversityController.text.isEmpty || element.otherUniversityError != null) {              setState(() {                element.otherUniversityError = _selectedScholarship?.acadmicCareer != 'DDS'                    ? localization.hsOtherUniversityValidate                    : localization.ddsOtherUniversityRequired;                firstErrorFocusNode ??= element.otherUniversityFocusNode;              });            }          }          /// #################################################################          /// major          if (element.majorController.text.isEmpty || element.majorError != null) {            setState(() {              element.majorError = _selectedScholarship?.acadmicCareer != 'DDS'                  ? localization.hsMajorValidate                  : localization.ddsMajorValidate;              firstErrorFocusNode ??= element.majorFocusNode;            });          }          /// #################################################################          /// cgpa          if (element.cgpaController.text.isEmpty || element.cgpaError != null) {            setState(() {              element.cgpaError = localization.cgpaValidate;              firstErrorFocusNode ??= element.cgpaFocusNode;            });          }          /// #################################################################          if (element.graduationStartDateController.text.isEmpty || element.graduationStartDateError != null) {            setState(() {              element.graduationStartDateError = localization.hsGraducationStartDateValidate;              firstErrorFocusNode ??= element.graduationStartDateFocusNode;            });          }          /// #################################################################          /// graduation end data          element.graduationEndDateError = null;          if ((!element.currentlyStudying && element.levelController.text.isNotEmpty)) {            if (element.graduationEndDateController.text.isEmpty || element.graduationEndDateError != null) {              setState(() {                element.graduationEndDateError = localization.hsGraducationEndDateValidate;                firstErrorFocusNode ??= element.graduationEndDateFocusNode;              });            }            if (element.graduationEndDateController.text == element.graduationStartDateController.text) {              setState(() {                element.graduationEndDateError = "${localization.hsGraducationEndDateValidate}\nPlease Enter correct start and end Date";                firstErrorFocusNode ??= element.graduationEndDateFocusNode;              });            }          }          /// #################################################################          /// Are you currently receiving scholarship or grant from other university          if (_selectedScholarship?.acadmicCareer == 'DDS') {            _selectSponsorshipErrorText = null;            if (havingSponsor.isEmpty) {              _selectSponsorshipErrorText = localization.ddsGradQuestion;              // _alertService.showErrorSnackBar(localization.ddsGradQuestion,);              firstErrorFocusNode = FocusNode();              return false;            }          }          /// #################################################################          /// sponsorship name          if ((havingSponsor == 'Y') ||              _selectedScholarship?.acadmicCareer != 'DDS') {            if (element.sponsorShipController.text.isEmpty || element.sponsorShipError != null) {              setState(() {                element.sponsorShipError = localization.hsSponsorshipValidate;                firstErrorFocusNode ??= element.sponsorShipFocusNode;              });            }          }          /// #################################################################          if (element.levelController.text == 'PGRD' ||              element.levelController.text == 'PG' ||              element.levelController.text == 'DDS') {            /// case study title            if (element.caseStudyTitleController.text.isEmpty || element.caseSudyError != null) {              setState(() {                element.caseStudyTitleError = localization.caseStudyTitleValidate;                firstErrorFocusNode ??= element.caseStudyTitleFocusNode;              });            }            /// case study start year            if (element.caseStudyStartYearController.text.isEmpty || element.caseStudyStartYearError != null) {              setState(() {                element.caseStudyStartYearError = localization.caseStudyStartYearValidate;                firstErrorFocusNode ??= element.caseStudyStartYearFocusNode;              });            }            /// case study description            if (element.caseStudyDescriptionController.text.isEmpty || element.caseStudyDescriptionError !=  null) {              setState(() {                element.caseStudyDescriptionError = localization.caseStudyDescriptionValidate;                firstErrorFocusNode ??= element.caseStudyDescriptionFocusNode;              });            }          }          /// #################################################################        }        if (_selectedScholarship?.acadmicCareer == 'UGRD' && element.currentlyStudying) {          validateGraduationDetails(element);        }        if (_selectedScholarship?.acadmicCareer != 'UGRD') {          validateGraduationDetails(element);        }      }    }    if (firstErrorFocusNode != null) {      FocusScope.of(context).requestFocus(firstErrorFocusNode);      return false;    } else {      /// No errors found, return true      await saveDraft();      return true;    }  }  Future<bool> validateUniversityAndMajorsDetails(langProvider)async {    final localization = AppLocalizations.of(context)!;    firstErrorFocusNode = null;    /// academic program pgrd    if (_selectedScholarship?.acadmicCareer == 'PGRD' &&        _selectedScholarship?.acadmicCareer != 'DDS') {      if (_acadProgramPgrdController.text.isEmpty || _acadProgramErrorText != null) {        setState(() {          _acadProgramPgrdErrorText = localization.pgrdAdacProgramRequired;          firstErrorFocusNode ??= _acadProgramPgrdFocusNode;        });      }}/// #################################################################    /// major    /// applicationForm.applicationData.acadCareer ne 'DDS' and rowIndex.index eq 0    if( _selectedScholarship?.acadmicCareer != 'DDS'){      for (int i = 0; i < _majorsWishlist.length; i++) {        var element = _majorsWishlist[i];        if (i == 0 && (element.majorController.text.isEmpty || element.majorError != null) ) {          setState(() {            element.majorError = localization.majorsValidate;            firstErrorFocusNode ??= element.majorFocusNode;          });        }        if (element.majorError != null) {          setState(() {            element.majorError = localization.majorsValidate;            firstErrorFocusNode ??= element.majorFocusNode;          });          if(element.majorController.text == 'OTH'){            if(element.otherMajorController.text.isEmpty || element.otherMajorError != null){              setState(() {                element.otherMajorError = localization.otherMajorValidate;                firstErrorFocusNode ??= element.otherMajorFocusNode;              });            }          }          }      }    }/// #################################################################    /// academic program dds    if (_selectedScholarship?.acadmicCareer == 'DDS') {      if (_acadProgramDdsController.text.isEmpty || _acadProgramDdsErrorText != null) {        setState(() {          _acadProgramDdsErrorText = localization.ddsMajorRequired;          firstErrorFocusNode ??= _acadProgramDdsFocusNode;        });      }    }    /// #################################################################    /// university wishlish validation    if (_selectedScholarship?.acadmicCareer != 'HCHL') {      for (int i = 0; i < _universityPriorityList.length; i++) {        var element = _universityPriorityList[i];        if (!isStudyCountry && element.countryIdController.text.isEmpty) {          setState(() {            element.countryIdError = localization.countryRequired;            firstErrorFocusNode ??= element.countryIdFocusNode;          });        }        /// #################################################################        /// On Web is paused also        // if (_selectedScholarship?.acadmicCareer != 'DDS') {        //   if (element.majorsController.text.isEmpty) {        //     setState(() {        //       element.majorsError = "Please Select Your Major";        //       firstErrorFocusNode ??= element.majorsFocusNode;        //     });        //   }        // }        /// #################################################################        /// validate other major        if(element.majorsController.text == 'OTH'){          if (_selectedScholarship?.acadmicCareer != 'DDS' &&              element.majorsController.text == 'OTH' &&              (element.countryIdController.text.isNotEmpty ||                  element.otherMajorsController.text.isNotEmpty ||                  element.otherUniversityNameController.text.isNotEmpty ||                  element.statusController.text.isNotEmpty)) {            if (element.majorsController.text.isEmpty) {              setState(() {                element.otherMajorsError = localization.ddsOtherMajorRequired;                firstErrorFocusNode ??= element.otherMajorsFocusNode;              });            }          }        }/// #################################################################//         / validate university//         / Also paused validation for university on web        if (_selectedScholarship?.acadmicCareer != 'DDS') {          if (element.universityIdController.text.isEmpty || element.universityIdError != null) {            setState(() {              element.universityIdError = localization.universityValidate;              firstErrorFocusNode ??= element.universityIdFocusNode;            });          }        }/// #################################################################        /// validate other university        if(element.universityIdController.text == 'OTH'){          if (_selectedScholarship?.acadmicCareer != 'DDS' &&              (element.countryIdController.text.isNotEmpty ||                  element.otherMajorsController.text.isNotEmpty ||                  element.otherUniversityNameController.text.isNotEmpty ||                  element.statusController.text.isNotEmpty)) {            if (element.otherUniversityNameController.text.isEmpty) {              setState(() {                element.otherUniversityNameError = localization.ddsOtherUniversityRequired;                firstErrorFocusNode ??= element.otherUniversityNameFocusNode;              });            }          }        }        /// #################################################################        if (_selectedScholarship?.acadmicCareer != 'DDS' &&            (element.countryIdController.text.isNotEmpty ||                element.otherMajorsController.text.isNotEmpty ||                element.otherUniversityNameController.text.isNotEmpty ||                element.statusController.text.isNotEmpty)) {          if (element.statusController.text.isEmpty) {            setState(() {              element.statusError = localization.universityStatusRequired;              firstErrorFocusNode ??= element.statusFocusNode;            });          }        }      }    }    /// checking for fist error node    if (firstErrorFocusNode != null) {      FocusScope.of(context).requestFocus(firstErrorFocusNode);      return false;    } else {      /// No errors found, return true      await saveDraft();      return true;    }  }  Future<bool> validateRequiredExaminations(langProvider) async{    final localization = AppLocalizations.of(context)!;    for (int i = 0; i < _requiredExaminationList.length; i++) {      firstErrorFocusNode = null;      var element = _requiredExaminationList[i];      if (element.examinationError != null ) {        setState(() {          element.examinationError = localization.duplicateExaminationMessage;          firstErrorFocusNode ??= element.examinationFocusNode;        });      }      if (element.examinationController.text.isNotEmpty && element.examinationTypeIdController.text.isEmpty && (element.examinationTypeDropdown?.isNotEmpty ?? false || element.examinationTypeDropdown != null)) {        setState(() {          element.examinationTypeIdError = localization.examinationTypeValidate;          firstErrorFocusNode ??= element.examinationTypeIdFocusNode;        });      }      // if (element.examinationGradeController.text.isEmpty) {      //   setState(() {      //     element.examinationGradeError = "Please Enter Examination Grade";      //     firstErrorFocusNode ??= element.examinationGradeFocusNode;      //   });      // }      if (element.examinationController.text.isNotEmpty && element.examDateController.text.isEmpty) {        setState(() {          element.examDateError = localization.dateExamValidate;          firstErrorFocusNode ??= element.examDateFocusNode;        });      }    }    /// checking for fist error node    if (firstErrorFocusNode != null) {      FocusScope.of(context).requestFocus(firstErrorFocusNode);      return false;    } else {      /// No errors found, return true      await saveDraft();      return true;    }  }  Future<bool> validateEmploymentHistory(langProvider) async{    final localization = AppLocalizations.of(context)!;    firstErrorFocusNode = null;    /// validate employment history    /// select employment status    _selectEmploymentHistoryErrorText = null;    if (_employmentStatus == null || _employmentStatus == '') {      _selectEmploymentHistoryErrorText = "${localization.previouslyEmployed} ?";      // _alertService.showToast(message: "${localization.previouslyEmployed} ?",          // context: context,      // );    }    if (_employmentStatus != null &&        _employmentStatus != '' &&        _employmentStatus != 'N') {      for (int i = 0; i < _employmentHistoryList.length; i++) {        var element = _employmentHistoryList[i];        /// employer name        if (element.employerNameController.text.isEmpty || element.employerNameError != null) {          setState(() {            element.employerNameError = localization.emphistEmployerNameValidate;            firstErrorFocusNode ??= element.employerNameFocusNode;          });        }        /// designation        if (element.titleController.text.isEmpty || element.titleError != null) {          setState(() {            element.titleError = localization.emphistTitleNameValidate;            firstErrorFocusNode ??= element.titleFocusNode;          });        }        ///work place        if (element.placeController.text.isEmpty || element.placeError != null) {          setState(() {            element.placeError = localization.emphistPlaceValidate;            firstErrorFocusNode ??= element.placeFocusNode;          });        }        ///occupation        if (element.occupationController.text.isEmpty || element.occupationError != null) {          setState(() {            element.occupationError = localization.emphistOccupationNameValidate;            firstErrorFocusNode ??= element.occupationFocusNode;          });        }        /// start date        if (element.startDateController.text.isEmpty || element.startDateError != null) {          setState(() {            element.startDateError = localization.employmentStartDateRequired;            firstErrorFocusNode ??= element.startDateFocusNode;          });        }        /// end date        if (_employmentStatus == 'P' &&           ( element.endDateController.text.isEmpty || element.endDateError != null)) {          setState(() {            element.endDateError = localization.employmentEndDateRequired;            firstErrorFocusNode ??= element.endDateFocusNode;          });        }        /// end date        if (element.endDateController.text.isNotEmpty && (element.endDateController.text == element.startDateController.text)) {          setState(() {            element.endDateError = "${localization.employmentEndDateRequired}\nPlease correct start and End Date";            firstErrorFocusNode ??= element.endDateFocusNode;          });        }        /// reporting manager        if (element.reportingManagerController.text.isEmpty || element.reportingManagerError != null) {          setState(() {            element.reportingManagerError = localization.emphistReportingManagerValidate;            firstErrorFocusNode ??= element.reportingManagerFocusNode;          });        }        /// contact number        if (element.contactNumberController.text.isEmpty || element.contactNumberError != null) {          setState(() {            element.contactNumberError = localization.emphistMgrContactNoValidate;            firstErrorFocusNode ??= element.contactNumberFocusNode;          });        }        /// email        if (element.contactEmailController.text.isEmpty || element.contactEmailError != null) {          setState(() {            element.contactEmailError = localization.registrationEmailAddressValidate;            firstErrorFocusNode ??= element.contactEmailFocusNode;          });        }      }    }    /// checking for fist error node    if (firstErrorFocusNode != null ||        _employmentStatus == null ||        _employmentStatus == '') {      FocusScope.of(context).requestFocus(firstErrorFocusNode);      return false;    } else {      /// No errors found, return true      await saveDraft();      return true;    }  }  bool isAttachmentRequiredForUser(Attachment attachment) {    if (_passportNationalityController.text == 'ARE' && (_isMotherUAECheckbox == false)) {      return attachment.requiredController.text == 'MRL';    } else if (_passportNationalityController.text != 'ARE' && (_isMotherUAECheckbox == false)) {      return attachment.requiredController.text == 'NMRL' ;    } else if (_passportNationalityController.text != 'ARE' && _isMotherUAECheckbox) {      return attachment.requiredController.text == 'XMRL';    }    return false;  }  showReqAttachmentName(langProvider,myAttachment) {    final localization = AppLocalizations.of(context);    _alertService.showErrorSnackBar(        "${localization?.uploadAttachments}: ${getFullNameFromLov(langProvider: langProvider,lovCode: _selectedCheckListCode,code:myAttachment.attachmentNameController.text)}"    );  }  Future<bool> validateAttachmentsSection(langProvider) async{    firstErrorFocusNode = null;    // print(_filteredMyAttachmentsList.length);    for(int index = 0; index < _filteredMyAttachmentsList.length; index++) {      // final attachment = _filteredAttachmentsList[index];      final myAttachment = _filteredMyAttachmentsList[index];      /// Only show the AttachFile widget if conditions are met      /// We are checking that if according to attachments requirements if the attachment is required then ask user to attach that if not then remove the item from list also.      /// For scholarship INTERNAL      if (isStudyCountry && isAttachmentRequiredForUser(myAttachment)) {        if(myAttachment.userFileNameController.text.isEmpty){          showReqAttachmentName(langProvider, myAttachment);          return false;        }      }      /// For scholarship EXTERNAL      if(!isStudyCountry){        final requiredText = myAttachment.requiredController.text;        if( myAttachment.userFileNameController.text.isEmpty && (requiredText == 'MRL' ||requiredText ==  'NMRL' ||requiredText ==  'XMRL')){        // if( myAttachment.userFileNameController.text.isEmpty){          showReqAttachmentName(langProvider, myAttachment);          return false;        }      }    }      await saveDraft();      return true; /// All required attachments are present  }  @override  void dispose() {    /// Dispose controllers    _militaryServiceController.dispose();    _militaryServiceStartDateController.dispose();    _militaryServiceEndDateController.dispose();    _reasonForMilitaryController.dispose();    /// Dispose focus nodes    _militaryServiceFocusNode.dispose();    _militaryServiceStartDateFocusNode.dispose();    _militaryServiceEndDateFocusNode.dispose();    _reasonForMilitaryFocusNode.dispose();    super.dispose();  }  /// *----------------------------------------------------------------------------- Custom Widgets for Scholarship Form only start --------------------------------------------------------------------------------------------------***  /// text field style which is used to styling hint and actual text  final TextStyle textFieldTextStyle = AppTextStyles.titleTextStyle().copyWith(      fontSize: 14,      fontWeight: FontWeight.w500,      color: AppColors.scoButtonColor,  );  /// Buttons Section (Previous, Next, Save Draft)  Widget draftPrevNextButtons(langProvider) {    return Directionality(textDirection: getTextDirection(langProvider), child: Padding(      padding: EdgeInsets.symmetric(